Window.Script:
/*
 * 프로그램명 : frmcust0010PSearchbuildingCheck.xml
 * 설명 : 동일한 대상물 체크 팝업
 * 작성일 : 2009.12.12
 * 작성자 : 이규희
 */

#include "LIB::cust.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
    
    // 공통 코드 조회
    gfn_SearchCommonCode(ds_oBizGubun, "BIZCD", "Y", "전체");
    
    if (IsExistVar("sBuildingNm")) {
        lfn_Search();
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oCustList.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("cust:searchCust0010BuildingInfo", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oCustList=ds_ioBuildingInfo"	// outDs
		,"BNM=" + quote(sBuildingNm)
		,"lfn_AfterSearchbuilding"
		,false
	);
}

/* 공통 필수 종료 */

/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchbuilding(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    //trace(ds_oCustList.SaveXML());

    gfn_SearchMessage(gdBuidingList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 이벤트 함수 종료 */


/* 사용자 함수 */

function btnSelect_OnClick(obj) {
    var arrArg = Array(1);
    arrArg[0] = ds_oCustList.GetColumn(ds_oCustList.currow,  "BMGNO");		        // 대상물관리번호 
    
    Close(arrArg);
}

function gdBuidingList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    btnSelect_OnClick(obj);
}

/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
