Window.Script:
/*
 * 프로그램명 : frmcust0010PTankInfo.xml
 * 설명 : 이동탱크부가정보
 * 작성일 : 2009.12.08
 * 작성자 : 이규희
 */

#include "LIB::cust.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    
    if (IsExistVar("sDelGubun")) {
        edCarNo.Enable = false;
        edCarYear.Enable = false;
        edPerMissionNo.Enable = false;
        edRemark.Enable = false;
        radPostGubun.Enable = false;
        cbxParkAddrGubun.Enable = false;
        edParkAddr1.Enable = false;
        edParkAddr2.Enable = false;
        edParkRoadAddr1.Enable = false;
        edParkRoadAddr2.Enable = false;
        cbxInstlrAddrGubun.Value = false;
        edInstlrAddr1.Enable = false;
        edInstlrAddr2.Enable = false;
        edInstlrRoadAddr1.Enable = false;
        edInstlrRoadAddr2.Enable = false;
        btnSearchParkZipCode.Enable = false;
        btnSearchInstlrZipCode.Enable = false;
        btnGateWayTank1.Enable = false;
        btnGateWayTank2.Enable = false;
        btnSave.Enable = false;
    }
    
    if (IsExistVar("sRow")) {
        lfn_Search(obj);
    } else {

		ds_ioTankInfo.ClearData();
		ds_ioTankInfo.AddRow();
		radPostGubun.Index = 0;
		cbxParkAddrGubun.Index = 0;
		cbxInstlrAddrGubun.Index = 0;
    }
    edCarNo.SetFocus();
}

function Form_OnUnloadCompleted(obj) {
    Close();
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 115 : // 저장(F4)
            lfn_Save();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
	ds_ioTankInfo.ClearData();
	ds_ioTankInfo.AddRow();
	
    ds_ioTankInfo.SetColumn(0, "TCARNO", Parent.ds_ioTankInfo.GetColumn(sRow, "TCARNO"));
    ds_ioTankInfo.SetColumn(0, "TCARYEAR", Parent.ds_ioTankInfo.GetColumn(sRow, "TCARYEAR"));
