    ds_ioTankInfo.SetColumn(0, "TINSTLRZIPCD", Parent.ds_ioTankInfo.GetColumn(sRow, "TINSTLRZIPCD"));
    ds_ioTankInfo.SetColumn(0, "TINSTLRADDR1", Parent.ds_ioTankInfo.GetColumn(sRow, "TINSTLRADDR1"));
    ds_ioTankInfo.SetColumn(0, "TINSTLRADDR2", Parent.ds_ioTankInfo.GetColumn(sRow, "TINSTLRADDR2"));    
    ds_ioTankInfo.SetColumn(0, "TINSTLRROADZIPCD", Parent.ds_ioTankInfo.GetColumn(sRow, "TINSTLRROADZIPCD"));
    ds_ioTankInfo.SetColumn(0, "TINSTLRROADADDR1", Parent.ds_ioTankInfo.GetColumn(sRow, "TINSTLRROADADDR1"));
