    ds_ioTankInfo.SetColumn(0, "TRPARKPNUCD", Parent.ds_ioTankInfo.GetColumn(sRow, "TRPARKPNUCD"));
    ds_ioTankInfo.SetColumn(0, "TRPARKROADBUILDMGNO", Parent.ds_ioTankInfo.GetColumn(sRow, "TRPARKROADBUILDMGNO"));
    ds_ioTankInfo.SetColumn(0, "TRPARKROADDETAIL", Parent.ds_ioTankInfo.GetColumn(sRow, "TRPARKROADDETAIL"));
    ds_ioTankInfo.SetColumn(0, "TRPARKROADREMAIN", Parent.ds_ioTankInfo.GetColumn(sRow, "TRPARKROADREMAIN"));
    ds_ioTankInfo.SetColumn(0, "TRPARKSANGUBUN", Parent.ds_ioTankInfo.GetColumn(sRow, "TRPARKSANGUBUN"));
    ds_ioTankInfo.SetColumn(0, "TRPARKMAINBUNJI", Parent.ds_ioTankInfo.GetColumn(sRow, "TRPARKMAINBUNJI"));
