    ds_ioTankInfo.SetColumn(0, "TRPARKBUILDNM", Parent.ds_ioTankInfo.GetColumn(sRow, "TRPARKBUILDNM"));
    ds_ioTankInfo.SetColumn(0, "TRPARKADDRETC", Parent.ds_ioTankInfo.GetColumn(sRow, "TRPARKADDRETC"));
    ds_ioTankInfo.SetColumn(0, "TRINSTLRPNUCD", Parent.ds_ioTankInfo.GetColumn(sRow, "TRINSTLRPNUCD"));
