/*
 * 기능 : 저장
 */
function lfn_Save(obj) {
/*    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
    */
    if (radPostGubun.Index == 0) {	// 청구지가 상치장소이면
		if (((cbxParkAddrGubun.Value == 0) && ((edParkAddr1.Text = "") || (edParkAddr2.Text = ""))) 
		 || ((cbxParkAddrGubun.Value == 1) && (edParkRoadAddr1.Text = ""))) {
				gfn_Alert("상치장소를 입력해 주십시오.");
				return false;
			}
	} else if (radPostGubun.Index == 1) {	// 청구지가 설치자주소이면
		if (((cbxInstlrAddrGubun.Value == 0) && ((edInstlrAddr1.Text = "") || (edInstlrAddr2.Text = ""))) 
		 || ((cbxInstlrAddrGubun.Value == 1) && (edInstlrRoadAddr1.Text = ""))) {
			gfn_Alert("설치자주소를 입력해 주십시오.");
			return false;
		}
	}
    
    
    if (IsExistVar("sRow")) {
        Parent.ds_ioTankInfo.SetColumn(sRow, "TBMGNO", Parent.ds_ioBuildingInfo.GetColumn(Parent.ds_ioBuildingInfo.currow, "BMGNO"));    
        Parent.ds_ioTankInfo.SetColumn(sRow, "TCARNO", edCarNo.Text);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TCARYEAR", edCarYear.Text);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TPERMISSIONNO", edPerMissionNo.Text);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TREMARK", edRemark.Text);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TPOSTREQGUBUN", radPostGubun.Value);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TPARKPLACEADDRGUBUN", cbxParkAddrGubun.Value);        
        Parent.ds_ioTankInfo.SetColumn(sRow, "TPARKPLACEZIPCD", medParkZipCode.Value);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TPARKPLACEADDR1", edParkAddr1.Text);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TPARKPLACEADDR2", edParkAddr2.Text);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TPARKPLACEROADZIPCD", medParkRoadZipCode.Value);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TPARKPLACEROADADDR1", edParkRoadAddr1.Text);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TPARKPLACEROADADDR2", edParkRoadAddr2.Text);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TINSTLRADDRGUBUN", cbxInstlrAddrGubun.Value);                
        Parent.ds_ioTankInfo.SetColumn(sRow, "TINSTLRZIPCD", medInstlrZipCode.Value);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TINSTLRADDR1", edInstlrAddr1.Text);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TINSTLRADDR2", edInstlrAddr2.Text);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TINSTLRROADZIPCD", medInstlrRoadZipCode.Value);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TINSTLRROADADDR1", edInstlrRoadAddr1.Text);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TINSTLRROADADDR2", edInstlrRoadAddr2.Text);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TGATEWAYTANK1", edGateWayTank1.Text);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TGATEWAYTANK2", edGateWayTank2.Text);
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRPARKPNUCD", ds_ioTankInfo.GetColumn(0, "TRPARKPNUCD"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRPARKROADBUILDMGNO", ds_ioTankInfo.GetColumn(0, "TRPARKROADBUILDMGNO"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRPARKROADDETAIL", ds_ioTankInfo.GetColumn(0, "TRPARKROADDETAIL"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRPARKROADREMAIN", ds_ioTankInfo.GetColumn(0, "TRPARKROADREMAIN"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRPARKSANGUBUN", ds_ioTankInfo.GetColumn(0, "TRPARKSANGUBUN"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRPARKMAINBUNJI", ds_ioTankInfo.GetColumn(0, "TRPARKMAINBUNJI"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRPARKSUBBUNJI", ds_ioTankInfo.GetColumn(0, "TRPARKSUBBUNJI"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRPARKBUILDNM", ds_ioTankInfo.GetColumn(0, "TRPARKBUILDNM"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRPARKADDRETC", ds_ioTankInfo.GetColumn(0, "TRPARKADDRETC"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRINSTLRPNUCD", ds_ioTankInfo.GetColumn(0, "TRINSTLRPNUCD"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRINSTLRROADBUILDMGNO", ds_ioTankInfo.GetColumn(0, "TRINSTLRROADBUILDMGNO"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRINSTLRROADDETAIL", ds_ioTankInfo.GetColumn(0, "TRINSTLRROADDETAIL"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRINSTLRROADREMAIN", ds_ioTankInfo.GetColumn(0, "TRINSTLRROADREMAIN"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRINSTLRSANGUBUN", ds_ioTankInfo.GetColumn(0, "TRINSTLRSANGUBUN"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRINSTLRMAINBUNJI", ds_ioTankInfo.GetColumn(0, "TRINSTLRMAINBUNJI"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRINSTLRSUBBUNJI", ds_ioTankInfo.GetColumn(0, "TRINSTLRSUBBUNJI"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRINSTLRBUILDNM", ds_ioTankInfo.GetColumn(0, "TRINSTLRBUILDNM"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRINSTLRADDRETC", ds_ioTankInfo.GetColumn(0, "TRINSTLRADDRETC"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRCONVPGM", ds_ioTankInfo.GetColumn(0, "TRCONVPGM"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRCONVPARKSTATUS", ds_ioTankInfo.GetColumn(0, "TRCONVPARKSTATUS"));
        Parent.ds_ioTankInfo.SetColumn(sRow, "TRCONVINSTLRSTATUS", ds_ioTankInfo.GetColumn(0, "TRCONVINSTLRSTATUS"));
        
        if (radPostGubun.Value == "0") {
			if (edParkRoadAddr1.Text == "") {			// 지번주소
				Parent.ds_ioTankInfo.SetColumn(sRow, "POSTPLACE", edParkAddr1.Text + " " + edParkAddr2.Text);
			} else {									// 도로명주소
				Parent.ds_ioTankInfo.SetColumn(sRow, "POSTPLACE", edParkRoadAddr1.Text + " " + edParkRoadAddr2.Text);
			}
        } else {
			if (edInstlrRoadAddr1.Text == "") {        // 지번주소
				Parent.ds_ioTankInfo.SetColumn(sRow, "POSTPLACE", edInstlrAddr1.Text + " " + edInstlrAddr2.Text);
			} else {									// 도로명주소
				Parent.ds_ioTankInfo.SetColumn(sRow, "POSTPLACE", edInstlrRoadAddr1.Text + " " + edInstlrRoadAddr2.Text);
			}
        }
        Close();
    } else {
        var nRow = Parent.ds_ioTankInfo.AddRow();
        Parent.ds_ioTankInfo.SetColumn(nRow, "TBMGNO", Parent.ds_ioBuildingInfo.GetColumn(Parent.ds_ioBuildingInfo.currow, "BMGNO"));    
        Parent.ds_ioTankInfo.SetColumn(nRow, "TCARNO", edCarNo.Text);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TCARYEAR", edCarYear.Text);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TPERMISSIONNO", edPerMissionNo.Text);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TREMARK", edRemark.Text);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TPOSTREQGUBUN", radPostGubun.Value);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TPARKPLACEADDRGUBUN", cbxParkAddrGubun.Value);        
        Parent.ds_ioTankInfo.SetColumn(nRow, "TPARKPLACEZIPCD", medParkZipCode.Value);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TPARKPLACEADDR1", edParkAddr1.Text);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TPARKPLACEADDR2", edParkAddr2.Text);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TPARKPLACEROADZIPCD", medParkRoadZipCode.Value);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TPARKPLACEROADADDR1", edParkRoadAddr1.Text);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TPARKPLACEROADADDR2", edParkRoadAddr2.Text);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TINSTLRADDRGUBUN", cbxInstlrAddrGubun.Value);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TINSTLRZIPCD", medInstlrZipCode.Value);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TINSTLRADDR1", edInstlrAddr1.Text);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TINSTLRADDR2", edInstlrAddr2.Text);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TINSTLRROADZIPCD", medInstlrRoadZipCode.Value);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TINSTLRROADADDR1", edInstlrRoadAddr1.Text);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TINSTLRROADADDR2", edInstlrRoadAddr2.Text);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TGATEWAYTANK1", edGateWayTank1.Text);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TGATEWAYTANK2", edGateWayTank2.Text);
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRPARKPNUCD", ds_ioTankInfo.GetColumn(0, "TRPARKPNUCD"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRPARKROADBUILDMGNO", ds_ioTankInfo.GetColumn(0, "TRPARKROADBUILDMGNO"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRPARKROADDETAIL", ds_ioTankInfo.GetColumn(0, "TRPARKROADDETAIL"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRPARKROADREMAIN", ds_ioTankInfo.GetColumn(0, "TRPARKROADREMAIN"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRPARKSANGUBUN", ds_ioTankInfo.GetColumn(0, "TRPARKSANGUBUN"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRPARKMAINBUNJI", ds_ioTankInfo.GetColumn(0, "TRPARKMAINBUNJI"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRPARKSUBBUNJI", ds_ioTankInfo.GetColumn(0, "TRPARKSUBBUNJI"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRPARKBUILDNM", ds_ioTankInfo.GetColumn(0, "TRPARKBUILDNM"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRPARKADDRETC", ds_ioTankInfo.GetColumn(0, "TRPARKADDRETC"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRINSTLRPNUCD", ds_ioTankInfo.GetColumn(0, "TRINSTLRPNUCD"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRINSTLRROADBUILDMGNO", ds_ioTankInfo.GetColumn(0, "TRINSTLRROADBUILDMGNO"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRINSTLRROADDETAIL", ds_ioTankInfo.GetColumn(0, "TRINSTLRROADDETAIL"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRINSTLRROADREMAIN", ds_ioTankInfo.GetColumn(0, "TRINSTLRROADREMAIN"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRINSTLRSANGUBUN", ds_ioTankInfo.GetColumn(0, "TRINSTLRSANGUBUN"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRINSTLRMAINBUNJI", ds_ioTankInfo.GetColumn(0, "TRINSTLRMAINBUNJI"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRINSTLRSUBBUNJI", ds_ioTankInfo.GetColumn(0, "TRINSTLRSUBBUNJI"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRINSTLRBUILDNM", ds_ioTankInfo.GetColumn(0, "TRINSTLRBUILDNM"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRINSTLRADDRETC", ds_ioTankInfo.GetColumn(0, "TRINSTLRADDRETC"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRCONVPGM", ds_ioTankInfo.GetColumn(0, "TRCONVPGM"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRCONVPARKSTATUS", ds_ioTankInfo.GetColumn(0, "TRCONVPARKSTATUS"));
        Parent.ds_ioTankInfo.SetColumn(nRow, "TRCONVINSTLRSTATUS", ds_ioTankInfo.GetColumn(0, "TRCONVINSTLRSTATUS"));
        
        if (radPostGubun.Value == "0") {
			if (cbxParkAddrGubun.Value == "0") {			// 지번주소
				Parent.ds_ioTankInfo.SetColumn(nRow, "POSTPLACE", edParkAddr1.Text + " " + edParkAddr2.Text);
			} else {									// 도로명주소
				Parent.ds_ioTankInfo.SetColumn(nRow, "POSTPLACE", edParkRoadAddr1.Text + " " + edParkRoadAddr2.Text);
			}
        } else {
			if (cbxInstlrAddrGubun.Value == "0") {        // 지번주소
				Parent.ds_ioTankInfo.SetColumn(nRow, "POSTPLACE", edInstlrAddr1.Text + " " + edInstlrAddr2.Text);
			} else {									// 도로명주소
				Parent.ds_ioTankInfo.SetColumn(nRow, "POSTPLACE", edInstlrRoadAddr1.Text + " " + edInstlrRoadAddr2.Text);
			}
        }
               
        Close();
    }    
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}
