{
    btnSearchParkZipCode.SetFocus();
}

// 우편번호(구주소)
function btnSearchParkZipCode_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if(nChar == 13) {	// Enter key
		btnSearchParkZipCode_OnClick();
	} else {
		gfn_ComponentOnKillFocus(obj);
	}
}

// 우편번호(구주소)
function btnSearchParkZipCode_OnClick(obj)
{
	if(cbxParkAddrGubun.Value == 0)
	{
		var sAddrGubun = 0;
		
		if (cbxParkAddrGubun.Index == 0) {	// 구주소
			sAddrGubun = 0;
		} else {
			sAddrGubun = 1;
		}
		
		var arrAddrInfo = gfn_PostRoadListPopup(Parent.cbxJibu.Value			// 지부코드
												, sAddrGubun						// 구새주소 구분[0:구주소, 1:새주소]
												, medParkZipCode.Value			// 구:지번 우편번호
												, edParkAddr1.Text		// 구:지번주소1
												, edParkAddr2.Text		// 구:지번주소2
												, ds_ioTankInfo.GetColumn(0, "TRPARKSANGUBUN")		// 구:산여부
												, ds_ioTankInfo.GetColumn(0, "TRPARKMAINBUNJI")	// 구:주번지
												, ds_ioTankInfo.GetColumn(0, "TRPARKSUBBUNJI")		// 구:부번지
												, ds_ioTankInfo.GetColumn(0, "TRPARKBUILDNM")		// 구:아파트,건물명
												, ds_ioTankInfo.GetColumn(0, "TRPARKADDRETC")	// 구:상세주소
												, medParkRoadZipCode.Value	// 새1:도로명 우편번호
												, edParkRoadAddr1.Text		// 새1:도로명주소11
												, edParkRoadAddr2.Text		// 새1:도로명주소12
												, ds_ioTankInfo.GetColumn(0, "TRPARKROADBUILDMGNO")	// 새1:건물관리번호1
												, ds_ioTankInfo.GetColumn(0, "TRPARKROADDETAIL")	// 새1:상세주소1
												, ds_ioTankInfo.GetColumn(0, "TRPARKROADREMAIN")		// 새1:부가주소1
												, medParkRoadZipCode.Value	// 새2:도로명 우편번호
												, edParkRoadAddr1.Text		// 새2:도로명주소21
												, edParkRoadAddr2.Text		// 새2:도로명주소22
												, ds_ioTankInfo.GetColumn(0, "TRPARKROADBUILDMGNO")	// 새2:건물관리번호2
												, ds_ioTankInfo.GetColumn(0, "TRPARKROADDETAIL")	// 새2:상세주소2
												, ds_ioTankInfo.GetColumn(0, "TRPARKROADREMAIN")	// 새2:부가주소2
												); 
				if (length(arrAddrInfo) > 0) {	
			if (arrAddrInfo[0] == 0) {
				
				/* 지번주소 조회 Tab */
				// 구주소
				medParkZipCode.Value = arrAddrInfo[1];		
				edParkAddr1.Text = arrAddrInfo[2];		
				edParkAddr2.Text = arrAddrInfo[3];
				
				// 새주소
				if (!gfn_IsNull(arrAddrInfo[10])) {
				
					medParkRoadZipCode.Value = arrAddrInfo[10];
					
				}
				
				edParkRoadAddr1.Text = arrAddrInfo[11];
				
				edParkRoadAddr2.Text = arrAddrInfo[12];		
			 
				ds_ioTankInfo.SetColumn(0, "TRPARKPNUCD", arrAddrInfo[9]);		// PNU 코드
				
				ds_ioTankInfo.SetColumn(0, "TRPARKROADBUILDMGNO", arrAddrInfo[13]);	// 건물관리번호
				
				ds_ioTankInfo.SetColumn(0, "TRPARKROADDETAIL", arrAddrInfo[14]);		// 상세주소
				
				ds_ioTankInfo.SetColumn(0, "TRPARKROADREMAIN", arrAddrInfo[15]);		// 부가주소
					   
				ds_ioTankInfo.SetColumn(0, "TRPARKSANGUBUN", arrAddrInfo[4]);
				
				ds_ioTankInfo.SetColumn(0, "TRPARKMAINBUNJI", arrAddrInfo[5]);
				
				ds_ioTankInfo.SetColumn(0, "TRPARKSUBBUNJI", arrAddrInfo[6]);
				
				ds_ioTankInfo.SetColumn(0, "TRPARKBUILDNM", arrAddrInfo[7]);
				
				ds_ioTankInfo.SetColumn(0, "TRPARKADDRETC", arrAddrInfo[8]);
	
				if (!gfn_IsNull(arrAddrInfo[11])) {	// 도로명주소1
				
					ds_ioTankInfo.SetColumn(0, "TRCONVPGM", "1");	// TRCONVPGM
					
					ds_ioTankInfo.SetColumn(0, "TRCONVPARKSTATUS", "01");	// TRCONVSTATUS
					
					cbxParkAddrGubun.Value = 1;
					
					cbxParkAddrGubun_OnChanged(obj, cbxParkAddrGubun.Value);
				
				} else {
				
					ds_ioTankInfo.SetColumn(0, "TRCONVPGM", "0");	// TRCONVPGM
					
					ds_ioTankInfo.SetColumn(0, "TRCONVPARKSTATUS", "00");	// TRCONVSTATUS
					
					cbxParkAddrGubun.Value = 0;
					
					cbxParkAddrGubun_OnChanged(obj, cbxParkAddrGubun.Value);
					
				}
	
			} else {
			
				/* 도로명주소 조회 Tab */
				medParkRoadZipCode.Value = arrAddrInfo[10];	// 우편번호		
				edParkRoadAddr1.Text = arrAddrInfo[11];		// 도로명주소1		
				edParkRoadAddr2.Text = arrAddrInfo[12];		// 도로명주소2
				
				ds_ioTankInfo.SetColumn(0, "TRPARKSANGUBUN", arrAddrInfo[4]);		// 산여부
				
				ds_ioTankInfo.SetColumn(0, "TRPARKMAINBUNJI", arrAddrInfo[5]);		// 주번지
				
				ds_ioTankInfo.SetColumn(0, "TRPARKSUBBUNJI", arrAddrInfo[6]);		// 부번지
				
				ds_ioTankInfo.SetColumn(0, "TRPARKPNUCD", arrAddrInfo[9]);		// PNU코드				
				
				ds_ioTankInfo.SetColumn(0, "TRPARKROADBUILDMGNO", arrAddrInfo[13]);	// 건물관리번호
				
				ds_ioTankInfo.SetColumn(0, "TRPARKROADDETAIL", arrAddrInfo[14]);		// 상세주소
				
				ds_ioTankInfo.SetColumn(0, "TRPARKROADREMAIN", arrAddrInfo[15]);		// 부가주소
	
				if (!gfn_IsNull(arrAddrInfo[11])) {	// 도로명주소1
				
					ds_ioTankInfo.SetColumn(0, "TRCONVPGM", "1");	// TRCONVPGM
					
					ds_ioTankInfo.SetColumn(0, "TRCONVPARKSTATUS", "01");	// TRCONVSTATUS
					
					cbxParkAddrGubun.Value = 1;
					
					cbxParkAddrGubun_OnChanged(obj, cbxParkAddrGubun.Value);
				
				} else {
				
					ds_ioTankInfo.SetColumn(0, "TRCONVPGM", "0");	// TRCONVPGM
					
					ds_ioTankInfo.SetColumn(0, "TRCONVPARKSTATUS", "00");	// TRCONVSTATUS
					
					cbxParkAddrGubun.Value = 0;
					
					cbxParkAddrGubun_OnChanged(obj, cbxParkAddrGubun.Value);
					
				}
			}
		}
	} 
	else 
	{
		var arrAddrInfo = gfn_PostRoadListPopup_New();
		if (length(arrAddrInfo) > 0) {
			ds_ioTankInfo.SetColumn(0, "TPARKPLACEADDRGUBUN", "1");
			medParkRoadZipCode.Value = arrAddrInfo[5];									// 우편번호
			edParkRoadAddr1.Text = arrAddrInfo[1];										// 도로명주소
			edParkRoadAddr2.Text = arrAddrInfo[29];										// 지번주소
			ds_ioTankInfo.SetColumn(0, "TRPARKSANGUBUN", 		arrAddrInfo[15]);	// 산여부(0:대지, 1:산)
			ds_ioTankInfo.SetColumn(0, "TRPARKMAINBUNJI", 		arrAddrInfo[22]);	// 주번지(지번주번지)
			ds_ioTankInfo.SetColumn(0, "TRPARKSUBBUNJI", 		arrAddrInfo[23]);	// 부번지(지번부번지, 부번지없을 경우 0)
			ds_ioTankInfo.SetColumn(0, "TRPARKPNUCD", "");						// PNU 코드 : 공백
			ds_ioTankInfo.SetColumn(0, "TRPARKROADBUILDMGNO", 	arrAddrInfo[8]);	// 건물관리번호
			ds_ioTankInfo.SetColumn(0, "TRPARKROADDETAIL", 		arrAddrInfo[1]);	// 상세주소(도로명주소)
			ds_ioTankInfo.SetColumn(0, "TRPARKROADREMAIN", "");					// 부가주소
			ds_ioTankInfo.SetColumn(0, "TRCONVPGM", "1");
			ds_ioTankInfo.SetColumn(0, "TRCONVPARKSTATUS", "01");
		}
	}
    
}

