{
	
	
	if(cbxInstlrAddrGubun.Value == 0)
	{
		var sAddrGubun = 0;
		
		if (cbxInstlrAddrGubun.Index == 0) {	// 구주소
			sAddrGubun = 0;
		} else {
			sAddrGubun = 1;
		}
		
		var arrAddrInfo = gfn_PostRoadListPopup(Parent.cbxJibu.Value			// 지부코드
												, sAddrGubun						// 구새주소 구분[0:구주소, 1:새주소]
												, medInstlrZipCode.Value			// 구:지번 우편번호
