												, edInstlrAddr2.Text		// 구:지번주소2
												, ds_ioTankInfo.GetColumn(0, "TRINSTLRSANGUBUN")		// 구:산여부
												, ds_ioTankInfo.GetColumn(0, "TRINSTLRMAINBUNJI")	// 구:주번지
												, ds_ioTankInfo.GetColumn(0, "TRINSTLRSUBBUNJI")		// 구:부번지
												, ds_ioTankInfo.GetColumn(0, "TRINSTLRBUILDNM")		// 구:아파트,건물명
												, ds_ioTankInfo.GetColumn(0, "TRINSTLRADDRETC")	// 구:상세주소
												, medInstlrRoadZipCode.Value	// 새1:도로명 우편번호
												, edInstlrRoadAddr1.Text		// 새1:도로명주소11
												, edInstlrRoadAddr2.Text		// 새1:도로명주소12
												, ds_ioTankInfo.GetColumn(0, "TRINSTLRROADBUILDMGNO")	// 새1:건물관리번호1
												, ds_ioTankInfo.GetColumn(0, "TRINSTLRROADDETAIL")	// 새1:상세주소1
												, ds_ioTankInfo.GetColumn(0, "TRINSTLRROADREMAIN")		// 새1:부가주소1
												, medInstlrRoadZipCode.Value	// 새2:도로명 우편번호
												, edInstlrRoadAddr1.Text		// 새2:도로명주소21
												, edInstlrRoadAddr2.Text		// 새2:도로명주소22
												, ds_ioTankInfo.GetColumn(0, "TRINSTLRROADBUILDMGNO")	// 새2:건물관리번호2
												, ds_ioTankInfo.GetColumn(0, "TRINSTLRROADDETAIL")	// 새2:상세주소2
												, ds_ioTankInfo.GetColumn(0, "TRINSTLRROADREMAIN")	// 새2:부가주소2
												); 
				if (length(arrAddrInfo) > 0) {	
			if (arrAddrInfo[0] == 0) {
				
				/* 지번주소 조회 Tab */
				// 구주소
				medInstlrZipCode.Value = arrAddrInfo[1];		
				edInstlrAddr1.Text = arrAddrInfo[2];		
				edInstlrAddr2.Text = arrAddrInfo[3];
				
				// 새주소
				if (!gfn_IsNull(arrAddrInfo[10])) {
				
					medInstlrRoadZipCode.Value = arrAddrInfo[10];
					
				}
				
				edInstlrRoadAddr1.Text = arrAddrInfo[11];
				
				edInstlrRoadAddr2.Text = arrAddrInfo[12];		
			 
				ds_ioTankInfo.SetColumn(0, "TRINSTLRPNUCD", arrAddrInfo[9]);		// PNU 코드
				
				ds_ioTankInfo.SetColumn(0, "TRINSTLRROADBUILDMGNO", arrAddrInfo[13]);	// 건물관리번호
				
				ds_ioTankInfo.SetColumn(0, "TRINSTLRROADDETAIL", arrAddrInfo[14]);		// 상세주소
				
				ds_ioTankInfo.SetColumn(0, "TRINSTLRROADREMAIN", arrAddrInfo[15]);		// 부가주소
					   
				ds_ioTankInfo.SetColumn(0, "TRINSTLRSANGUBUN", arrAddrInfo[4]);
				
				ds_ioTankInfo.SetColumn(0, "TRINSTLRMAINBUNJI", arrAddrInfo[5]);
				
				ds_ioTankInfo.SetColumn(0, "TRINSTLRSUBBUNJI", arrAddrInfo[6]);
				
				ds_ioTankInfo.SetColumn(0, "TRINSTLRBUILDNM", arrAddrInfo[7]);
				
				ds_ioTankInfo.SetColumn(0, "TRINSTLRADDRETC", arrAddrInfo[8]);
	
				if (!gfn_IsNull(arrAddrInfo[11])) {	// 도로명주소1
				
					ds_ioTankInfo.SetColumn(0, "TRCONVPGM", "1");	// TRCONVPGM
					
					ds_ioTankInfo.SetColumn(0, "TRCONVINSTLRSTATUS", "01");	// TRCONVSTATUS
					
					cbxInstlrAddrGubun.Value = 1;
					
					cbxInstlrAddrGubun_OnChanged(obj, cbxInstlrAddrGubun.Value);
	
				
				} else {
				
					ds_ioTankInfo.SetColumn(0, "TRCONVPGM", "0");	// TRCONVPGM
					
					ds_ioTankInfo.SetColumn(0, "TRCONVINSTLRSTATUS", "00");	// TRCONVSTATUS
					
					cbxInstlrAddrGubun.Value = 0;
					
					cbxInstlrAddrGubun_OnChanged(obj, cbxInstlrAddrGubun.Value);
					
				}
	
			} else {
			
				/* 도로명주소 조회 Tab */
				medInstlrRoadZipCode.Value = arrAddrInfo[10];	// 우편번호		
				edInstlrRoadAddr1.Text = arrAddrInfo[11];		// 도로명주소1		
				edInstlrRoadAddr2.Text = arrAddrInfo[12];		// 도로명주소2
				
				ds_ioTankInfo.SetColumn(0, "TRINSTLRSANGUBUN", arrAddrInfo[4]);		// 산여부
				
				ds_ioTankInfo.SetColumn(0, "TRINSTLRMAINBUNJI", arrAddrInfo[5]);		// 주번지
				
				ds_ioTankInfo.SetColumn(0, "TRINSTLRSUBBUNJI", arrAddrInfo[6]);		// 부번지
				
				ds_ioTankInfo.SetColumn(0, "TRINSTLRPNUCD", arrAddrInfo[9]);		// PNU코드				
				
				ds_ioTankInfo.SetColumn(0, "TRINSTLRROADBUILDMGNO", arrAddrInfo[13]);	// 건물관리번호
				
				ds_ioTankInfo.SetColumn(0, "TRINSTLRROADDETAIL", arrAddrInfo[14]);		// 상세주소
				
				ds_ioTankInfo.SetColumn(0, "TRINSTLRROADREMAIN", arrAddrInfo[15]);		// 부가주소
				
				if (!gfn_IsNull(arrAddrInfo[11])) {	// 도로명주소1
				
					ds_ioTankInfo.SetColumn(0, "TRCONVPGM", "1");	// TRCONVPGM
					
					ds_ioTankInfo.SetColumn(0, "TRCONVINSTLRSTATUS", "01");	// TRCONVSTATUS
					
					cbxInstlrAddrGubun.Value = 1;
					
					cbxInstlrAddrGubun_OnChanged(obj, cbxInstlrAddrGubun.Value);
				
				} else {
				
					ds_ioTankInfo.SetColumn(0, "TRCONVPGM", "0");	// TRCONVPGM
					
					ds_ioTankInfo.SetColumn(0, "TRCONVINSTLRSTATUS", "00");	// TRCONVSTATUS
					
					cbxInstlrAddrGubun.Value = 0;
					
					cbxInstlrAddrGubun_OnChanged(obj, cbxInstlrAddrGubun.Value);
					
				}
				
			}
	
		}
	}
	else 
	{
		var arrAddrInfo = gfn_PostRoadListPopup_New();
		if (length(arrAddrInfo) > 0) {
			radAddrGubun.Value = 1;	// 도로명주소
			ds_ioTankInfo.SetColumn(0, "TINSTLRADDRGUBUN", "1");
			medInstlrRoadZipCode.Value = arrAddrInfo[5];									// 우편번호
			edInstlrRoadAddr1.Text = arrAddrInfo[1];										// 도로명주소
			edInstlrRoadAddr2.Text = arrAddrInfo[29];										// 지번주소
			ds_ioTankInfo.SetColumn(0, "TRINSTLRSANGUBUN", 		arrAddrInfo[15]);	// 산여부(0:대지, 1:산)
			ds_ioTankInfo.SetColumn(0, "TRINSTLRMAINBUNJI", 		arrAddrInfo[22]);	// 주번지(지번주번지)
			ds_ioTankInfo.SetColumn(0, "TRINSTLRSUBBUNJI", 		arrAddrInfo[23]);	// 부번지(지번부번지, 부번지없을 경우 0)
			ds_ioTankInfo.SetColumn(0, "TRINSTLRPNUCD", "");							// PNU 코드 : 공백
			ds_ioTankInfo.SetColumn(0, "TRINSTLRROADBUILDMGNO", 	arrAddrInfo[8]);	// 건물관리번호
			ds_ioTankInfo.SetColumn(0, "TRINSTLRROADDETAIL", 		arrAddrInfo[1]);	// 상세주소(도로명주소)
			ds_ioTankInfo.SetColumn(0, "TRINSTLRROADREMAIN", "");						// 부가주소
			ds_ioTankInfo.SetColumn(0, "TRCONVPGM", "1");
			ds_ioTankInfo.SetColumn(0, "TRCONVINSTLRSTATUS", "01");
		}
	}
}

