#include "LIB::cust.js"; // 공통업무 관련


function Form_OnLoadCompleted(obj)
{
	gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
		
	obj.Title = "보류사유";
	lbTitle.Text = "보류사유";
	lbMeg.Text = "보류사유를 입력바랍니다";
	
}


/* 확인 버튼 처리*/
function btnConfirm_OnClick(obj)
{
	// 체크 
	if (edRemark.Text == null || edRemark.Text == "") {
		alert(lbTitle.Text + "를 입력 해주세요");
		edRemark.SetFocus();
		return;
	}

	var arrArg = edRemark.Text;
    
    Close(arrArg);
}


function btnCancel_OnClick(obj)
{
	var arrArg = "";
    
    Close(arrArg);
}

function edKeyWord_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
    if (nChar == 13) {
        btnConfirm_OnClick(obj);
    }	
}
