var mb_IsNewData = true; // 저장버튼이 클릭되었는지 여부

var arrRoadAddrArg = Array(10);	// 도로명주소 관련
/*  0 ARPNUCD,
    1 AROADBUILDMGNO, 
    2 ARROADDETAIL, 
    3 ARROADREMAIN, 
    4 ARSANGUBUN, 
    5 ARMAINBUNJI, 
    6 ARSUBBUNJI, 
    7 ARBUILDNM, 
    8 ARADDRETC, 
