    10 ARCONVSTATUS*/

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    // 공통 코드 조회    
    if (IsExistVar("sIsPopup")) {   // sKeyword , sGubun 인자값
        IsPopup = sIsPopup;
    }
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdAgentList.Width = nCx - gdAgentList.Left - 8;
        Shape2.Width = nCx - Shape2.Left - 4;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    
    ds_oAgentList.ClearData(); // 데이타셋 초기화
    
    // 조회이력을 저장한다.  
    ls_HistoryArg = "검색조건=" + quote(radAgent.Text)
                + ",검색어=" + quote(edKeyWord.Text);
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );
    
    medTotalCnt.Value = "";

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust0040AgentList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oAgentList=ds_oAgentList"	// outDs
		,"GUBUN=" + quote(radAgent.Value)
            + " KEYWORD=" + quote(edKeyWord.Text)	// args 
		,"lfn_AfterSearchAgentList"
		,false
	);
}

/*
 * 기능 : 추가
 */
function lfn_Input(obj) {
	if (!gfn_Confirm(MSGI00070)) { // 신규 추가 하시겠습니까?\n입력완료 후 저장 버튼을 누르세요.
        return false;
	}

	mb_IsNewData = true; // 저장버튼이 클릭 설정
    //lfn_Cancel(); // 값 초기화

    // 기본값 설정
    edAgnm.Text = "";
    edOwner.Text = "";
    medCono.Value = "";
    edTel.Text = "";
    edFax.Text = "";
    medZipCode.Value = "";
    edAddr1.Text = "";
    edAddr2.Text = "";
    edRemark.Text = "";
    //cbxAddrGubun.Value = 0;
    // 도로명주소를 기본으로 한다.
    cbxAddrGubun.Value = 1;

    // 도로명주소 정보 초기화
    for (var i=0; i<=10; i++) {
		arrRoadAddrArg[i] = "";
	}

    edAgnm.SetFocus();
}

/*
 * 기능 : 삭제
 */
function lfn_Delete(obj) {
    if (ds_oAgentList.currow < 0) {
        gfn_Alert(MSGI00030); // 삭제할 레코드가 없습니다.
        return false;
    }
    
	if (!gfn_Confirm(MSGW00030)) { // 정말로 삭제 하시겠습니까?
        return false;
	}
	
	lfn_SaveModifyHistory("D"); // 변경이력을 저장한다.

    ds_oAgentList.DeleteRow(ds_oAgentList.currow); // 선택된 ROW를 삭제한다.

    gfn_FormInit(this); // 입력란 초기화

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""	// insert
		, ""	// update
		, "cust:deleteCust0040AgentInfo"	// delete
		, ""	// flag column 명
		, ""	// key sql id
		, 0	// key 증가 값
		, "" 
		, 0 
		, "N"	// 실행타입
	);
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iAgentList=ds_oAgentList:U"	// inDs
		,""	// outDs
		,""	// args 
		,"lfn_AfterSaveAgentInfo"
		,false
	);
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }

    if (mb_IsNewData) { // 신규 저장
        // 키 중복 체크
    
        if (!gfn_Confirm(MSGW00020)) { // 정말로 추가 저장 하시겠습니까?
            return false;
        }
        
        // 입력된 값을 그리드로 복사한다.
        ds_oAgentList.InsertRow(0);
        ds_oAgentList.SetColumn(0, "AGNM", edAgnm.Text);
        ds_oAgentList.SetColumn(0, "AGOWNER", edOwner.Text);              
        ds_oAgentList.SetColumn(0, "AGCONO", medCono.Value);             
        ds_oAgentList.SetColumn(0, "AGTEL", edTel.Text);                  
        ds_oAgentList.SetColumn(0, "AGFAX", edFax.Text);      
        ds_oAgentList.SetColumn(0, "AGZIPCD", medZipCode.Value);       
        ds_oAgentList.SetColumn(0, "AGADDR1", edAddr1.Text);                        
        ds_oAgentList.SetColumn(0, "AGADDR2", edAddr2.Text);            
        ds_oAgentList.SetColumn(0, "AGREMARK", edRemark.Text);
        ds_oAgentList.SetColumn(0, "AGADDRGUBUN", cbxAddrGubun.Value);
        ds_oAgentList.SetColumn(0, "ARPNUCD", arrRoadAddrArg[0]);
        ds_oAgentList.SetColumn(0, "ARROADBUILDMGNO", arrRoadAddrArg[1]);
        ds_oAgentList.SetColumn(0, "ARROADDETAIL", arrRoadAddrArg[2]);
        ds_oAgentList.SetColumn(0, "ARROADREMAIN", arrRoadAddrArg[3]);
        ds_oAgentList.SetColumn(0, "ARSANGUBUN", arrRoadAddrArg[4]);
        ds_oAgentList.SetColumn(0, "ARMAINBUNJI", arrRoadAddrArg[5]);
        ds_oAgentList.SetColumn(0, "ARSUBBUNJI", arrRoadAddrArg[6]);
        ds_oAgentList.SetColumn(0, "ARBUILDNM", arrRoadAddrArg[7]);
        ds_oAgentList.SetColumn(0, "ARADDRETC", arrRoadAddrArg[8]);
        if (!gfn_IsNull(arrRoadAddrArg[1])) {
			ds_oAgentList.SetColumn(0, "ARCONVPGM", "1");
			ds_oAgentList.SetColumn(0, "ARCONVSTATUS", "01");
		} else {
			ds_oAgentList.SetColumn(0, "ARCONVPGM", "0");
			ds_oAgentList.SetColumn(0, "ARCONVSTATUS", "00");
		}
        
        lfn_SaveModifyHistory("I"); // 변경이력을 저장한다.
    } else { // 수정 저장
        if (!gfn_Confirm(MSGW00040)) { // 정말로 수정 저장 하시겠습니까?
            return false;
        }

        // 입력된 값을 그리드로 복사한다.
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "AGNM", edAgnm.Text);
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "AGOWNER", edOwner.Text);              
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "AGCONO", medCono.Value);             
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "AGTEL", edTel.Text);                  
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "AGFAX", edFax.Text);      
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "AGZIPCD", medZipCode.Value);       
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "AGADDR1", edAddr1.Text);                        
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "AGADDR2", edAddr2.Text);            
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "AGREMARK", edRemark.Text);
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "AGADDRGUBUN", cbxAddrGubun.Value);
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "ARPNUCD", arrRoadAddrArg[0]);
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "ARROADBUILDMGNO", arrRoadAddrArg[1]);
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "ARROADDETAIL", arrRoadAddrArg[2]);
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "ARROADREMAIN", arrRoadAddrArg[3]);
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "ARSANGUBUN", arrRoadAddrArg[4]);
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "ARMAINBUNJI", arrRoadAddrArg[5]);
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "ARSUBBUNJI", arrRoadAddrArg[6]);
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "ARBUILDNM", arrRoadAddrArg[7]);
        ds_oAgentList.SetColumn(ds_oAgentList.currow, "ARADDRETC", arrRoadAddrArg[8]);
        if (!gfn_IsNull(arrRoadAddrArg[1])) {
			ds_oAgentList.SetColumn(ds_oAgentList.currow, "ARCONVPGM", "1");
			ds_oAgentList.SetColumn(ds_oAgentList.currow, "ARCONVSTATUS", "01");
		} else {
			ds_oAgentList.SetColumn(ds_oAgentList.currow, "ARCONVPGM", "0");
			ds_oAgentList.SetColumn(ds_oAgentList.currow, "ARCONVSTATUS", "00");
		}
                
        lfn_SaveModifyHistory("U"); // 변경이력을 저장한다.
        
    }

    gfn_FormInit(this); // 입력란 초기화

    tit_clearActionInfo();
    tit_addSaveActionInfo(
        "cust:insertCust0040AgentInfo"	// insert
        , "cust:updateCust0040AgentInfo"	// update
        , ""	// delete
        , ""	// flag column 명
        , ""	// key sql id
        , 0	// key 증가 값
        , "" 
        , 0 
        , "N"	// 실행타입
    );
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,"ds_iAgentList=ds_oAgentList:U"	// inDs
        ,""	// outDs
        ,""	// args 
        ,"lfn_AfterSaveAgentInfo"
        ,false
    );
}
