function ds_oAgentList_OnRowPosChanged(obj,nOldRow,nRow)
{
    // 그리드의 값을 입력란에 복사한다. 추가상태인 경우 복사 후 저장을 하기 위해 제외한다.
    if (obj.GetRowType(nRow) <> "insert" && nRow <> -1) {
        edAgnm.Text = obj.GetColumn(nRow, "AGNM");
        edOwner.Text = obj.GetColumn(nRow, "AGOWNER");
        medCono.Value = obj.GetColumn(nRow, "AGCONO");
        edTel.Text = obj.GetColumn(nRow, "AGTEL");
        edFax.Text = obj.GetColumn(nRow, "AGFAX");        
        medZipCode.Value = obj.GetColumn(nRow, "AGZIPCD");
        edAddr1.Text = obj.GetColumn(nRow, "AGADDR1");
        edAddr2.Text = obj.GetColumn(nRow, "AGADDR2");
        edRemark.Text = obj.GetColumn(nRow, "AGREMARK");
        cbxAddrGubun.Value = obj.GetColumn(nRow, "AGADDRGUBUN");
        arrRoadAddrArg[0] = obj.GetColumn(nRow, "ARPNUCD");
        arrRoadAddrArg[1] = obj.GetColumn(nRow, "ARROADBUILDMGNO");
        arrRoadAddrArg[2] = obj.GetColumn(nRow, "ARROADDETAIL");
        arrRoadAddrArg[3] = obj.GetColumn(nRow, "ARROADREMAIN");
        arrRoadAddrArg[4] = obj.GetColumn(nRow, "ARSANGUBUN");
        arrRoadAddrArg[5] = obj.GetColumn(nRow, "ARMAINBUNJI");
        arrRoadAddrArg[6] = obj.GetColumn(nRow, "ARSUBBUNJI");
        arrRoadAddrArg[7] = obj.GetColumn(nRow, "ARBUILDNM");
        arrRoadAddrArg[8] = obj.GetColumn(nRow, "ARADDRETC");
        arrRoadAddrArg[9] = obj.GetColumn(nRow, "ARCONVPGM");
        arrRoadAddrArg[10] = obj.GetColumn(nRow, "ARCONVSTATUS");
	}
	mb_IsNewData = false; // 저장버튼이 클릭 초기화
}

function gdAgentList_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	ds_oAgentList_OnRowPosChanged(ds_oAgentList,nRow,nRow);
}

function btnToExcel_OnClick(obj) {
    if (ds_oAgentList.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return false;
    }

	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1405";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_oAgentList.GetRowCount();
		parValue += " sTitle=" + quote(gdAgentList.UserData);
	var ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}
	lfn_Excel(obj);
}

/*
 * 기능 : 업무 변경이력을 저장한다.
 * parameter : sGubun - 변경구분. 예) I, U, D
 * return : 없음
 */
function lfn_SaveModifyHistory(sGubun) {
    if (sGubun = "I") { // 신규
        gds_iModifyHistory.InsertRow(0);
        gds_iModifyHistory.SetColumn(0, "MHWORKGUBUN", "업무대행업체"); // 업무구분
        gds_iModifyHistory.SetColumn(0, "MHGUBUN", sGubun); // 변경구분
        gds_iModifyHistory.SetColumn(0, "MHKEY", ""); // KEY값
        gds_iModifyHistory.SetColumn(0, "MHCOLUMN", ""); // 변경 컬럼명
        gds_iModifyHistory.SetColumn(0, "MHBEFOREDATA", ""); // 변경 전 값
        gds_iModifyHistory.SetColumn(0, "MHAFTERDATA", ""); // 변경 후 값
    } else if (sGubun = "D") { // 삭제
        gds_iModifyHistory.InsertRow(0);
        gds_iModifyHistory.SetColumn(0, "MHWORKGUBUN", "업무대행업체"); // 업무구분
        gds_iModifyHistory.SetColumn(0, "MHGUBUN", sGubun); // 변경구분
        gds_iModifyHistory.SetColumn(0, "MHKEY", ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGMGNO")); // KEY값
        gds_iModifyHistory.SetColumn(0, "MHCOLUMN", ""); // 변경 컬럼명
        gds_iModifyHistory.SetColumn(0, "MHBEFOREDATA", ""); // 변경 전 값
        gds_iModifyHistory.SetColumn(0, "MHAFTERDATA", ""); // 변경 후 값
    } else if (sGubun = "U") { // 수정
        var ln_TotalCount = ds_oModifyColumn.GetCount();
        var ls_ColumnCd = "";
        var ls_ColumnNm = "";
        for (var i = 0; i < ln_TotalCount; i++) {
            ls_ColumnCd = ds_oModifyColumn.GetColumn(i, "COLUMNCD");
            ls_ColumnNm = ds_oModifyColumn.GetColumn(i, "COLUMNNM");
            if (ds_oAgentList.GetOrgBuffColumn(0, ls_ColumnCd) <> ds_oAgentList.GetColumn(ds_oAgentList.currow, ls_ColumnCd)) {
                gds_iModifyHistory.InsertRow(0);
                gds_iModifyHistory.SetColumn(0, "MHWORKGUBUN", "업무대행업체"); // 업무구분
                gds_iModifyHistory.SetColumn(0, "MHGUBUN", sGubun); // 변경구분
                gds_iModifyHistory.SetColumn(0, "MHKEY", ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGMGNO")); // KEY값
                gds_iModifyHistory.SetColumn(0, "MHCOLUMN", ls_ColumnNm); // 변경 컬럼명
                gds_iModifyHistory.SetColumn(0, "MHBEFOREDATA", ds_oAgentList.GetOrgBuffColumn(0, ls_ColumnCd)); // 변경 전 값
                gds_iModifyHistory.SetColumn(0, "MHAFTERDATA", ds_oAgentList.GetColumn(ds_oAgentList.currow, ls_ColumnCd)); // 변경 후 값
            }
        }
    }

    gfn_InsertModifyHistory(); // 업무 변경이력을 저장한다.
}

// 우편번호
function medZipCode_OnFocus(obj)
{
    btnSearchZipCode.SetFocus();
}

// 우편번호
function btnSearchZipCode_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if(nChar == 13) {	// Enter key
		btnSearchZipCode_OnClick();
	} else {
		gfn_ComponentOnKillFocus(obj);
	}
}

// 우편번호
function btnSearchZipCode_OnClick(obj)
{
	var sAddrGubun = 0;
	
	if (cbxAddrGubun.Index == 0) {	// 구주소
		sAddrGubun = 0;
	} else {
		sAddrGubun = 1;
	}
    
    if(cbxAddrGubun.Value == 0)
    {
		// 지번주소/도로명주소 조회 팝업
		var arrAddrInfo = gfn_PostRoadListPopup(gds_oUserInfo.GetColumn(0,"PDEPTCD")		// 지부코드
												, sAddrGubun		// 구새주소 구분[0:구주소, 1:새주소]
												, medZipCode.Value	// 구:지번 우편번호
												, edAddr1.Text		// 구:지번주소1
												, edAddr2.Text		// 구:지번주소2
												, ds_oAgentList.GetColumn(ds_oAgentList.currow, "ARSANGUBUN")	// 구:산여부
												, ds_oAgentList.GetColumn(ds_oAgentList.currow, "ARMAINBUNJI")	// 구:주번지
												, ds_oAgentList.GetColumn(ds_oAgentList.currow, "ARSUBBUNJI")	// 구:부번지
												, ds_oAgentList.GetColumn(ds_oAgentList.currow, "ARBUILDNM")	// 구:아파트,건물명
												, ds_oAgentList.GetColumn(ds_oAgentList.currow, "ARADDRETC")	// 구:상세주소
												, iif(ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGADDRGUBUN")=="0", "", ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGZIPCD"))	// 새1:도로명 우편번호
												, iif(ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGADDRGUBUN")=="0", "", ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGADDR1"))		// 새1:도로명주소11
												, iif(ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGADDRGUBUN")=="0", "", ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGADDR1"))		// 새1:도로명주소12
												, iif(ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGADDRGUBUN")=="0", "", ds_oAgentList.GetColumn(ds_oAgentList.currow, "ARROADBUILDMGNO"))	// 새1:건물관리번호1
												, iif(ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGADDRGUBUN")=="0", "", ds_oAgentList.GetColumn(ds_oAgentList.currow, "ARROADDETAIL"))	// 새1:상세주소1
												, iif(ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGADDRGUBUN")=="0", "", ds_oAgentList.GetColumn(ds_oAgentList.currow, "ARROADREMAIN"))		// 새1:부가주소1
												, iif(ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGADDRGUBUN")=="0", "", ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGZIPCD"))	// 새2:도로명 우편번호
												, iif(ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGADDRGUBUN")=="0", "", ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGADDR1"))		// 새2:도로명주소21
												, iif(ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGADDRGUBUN")=="0", "", ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGADDR1"))		// 새2:도로명주소22
												, iif(ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGADDRGUBUN")=="0", "", ds_oAgentList.GetColumn(ds_oAgentList.currow, "ARROADBUILDMGNO"))	// 새2:건물관리번호2
												, iif(ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGADDRGUBUN")=="0", "", ds_oAgentList.GetColumn(ds_oAgentList.currow, "ARROADDETAIL"))	// 새2:상세주소2
												, iif(ds_oAgentList.GetColumn(ds_oAgentList.currow, "AGADDRGUBUN")=="0", "", ds_oAgentList.GetColumn(ds_oAgentList.currow, "ARROADREMAIN"))	// 새2:부가주소2
												); 	
		if (length(arrAddrInfo) > 0) {	
			if (arrAddrInfo[0] == 0) {
				
				/* 지번주소 조회 Tab */
				// 구주소
				cbxAddrGubun.Value = 0;
				
				medZipCode.Value = arrAddrInfo[1];		
				edAddr1.Text = arrAddrInfo[2];		
				edAddr2.Text = arrAddrInfo[3];
				
				if (arrAddrInfo[11] <> "") {
					
					// 새주소
					cbxAddrGubun.Value = 1;
					
					medZipCode.Value = arrAddrInfo[10];
					
					edAddr1.Text = arrAddrInfo[11];
					
					edAddr2.Text = arrAddrInfo[12];
									}
			 
				arrRoadAddrArg[0] = arrAddrInfo[9];		// PNU 코드
				
				arrRoadAddrArg[1] = arrAddrInfo[13];	// 건물관리번호
				
				arrRoadAddrArg[2] = arrAddrInfo[14];		// 상세주소
				
				arrRoadAddrArg[3] = arrAddrInfo[15];		// 부가주소
					   
				arrRoadAddrArg[4] = arrAddrInfo[4];		// 산번지 여부
				
				arrRoadAddrArg[5] = arrAddrInfo[5];		// 주번지
				
				arrRoadAddrArg[6] = arrAddrInfo[6];		// 부번지
				
				arrRoadAddrArg[7] = arrAddrInfo[7];		// 건물명
				
				arrRoadAddrArg[8] = arrAddrInfo[8];		// 기타주소
				
				if (!gfn_IsNull(arrAddrInfo[11])) {	// 도로명주소1
				
					arrRoadAddrArg[9] = "1";	// ARCONVPGM
					
					arrRoadAddrArg[10] = "01";	// ARCONVSTATUS
				
				} else {
				
					arrRoadAddrArg[9] = "0";
					
					arrRoadAddrArg[10] = "00";
					
				}
				
			} else {
			
				/* 도로명주소 조회 Tab */
				
				cbxAddrGubun.Value = 1;
			
				medZipCode.Value = arrAddrInfo[10];	// 우편번호		
				edAddr1.Text = arrAddrInfo[11];		// 도로명주소1		
				edAddr2.Text = arrAddrInfo[12];		// 도로명주소2
				
				arrRoadAddrArg[4] = arrAddrInfo[4];		// 산여부
				
				arrRoadAddrArg[5] = arrAddrInfo[5];		// 주번지
				
				arrRoadAddrArg[6] = arrAddrInfo[6];		// 부번지
				
				arrRoadAddrArg[0] = arrAddrInfo[9];		// PNU 코드
				
				arrRoadAddrArg[1] = arrAddrInfo[13];	// 건물관리번호
				
				arrRoadAddrArg[2] = arrAddrInfo[14];		// 상세주소
				
				arrRoadAddrArg[3] = arrAddrInfo[15];		// 부가주소
		
				if (!gfn_IsNull(arrAddrInfo[11])) {	// 도로명주소1
				
					arrRoadAddrArg[9] = "1";	// ARCONVPGM
					
					arrRoadAddrArg[10] = "01";	// ARCONVSTATUS
				
				} else {
				
					arrRoadAddrArg[9] = "0";
					
					arrRoadAddrArg[10] = "00";
					
				}
		
			}
	
		}
	}
	else 
	{
		var arrAddrInfo = gfn_PostRoadListPopup_New();
		if (length(arrAddrInfo) > 0) {
			cbxAddrGubun.Value = 1;
			medZipCode.Value = arrAddrInfo[5];		// 우편번호			edAddr1.Text = arrAddrInfo[1];			// 도로명주소1		
			edAddr2.Text = arrAddrInfo[29];			// 도로명주소2
			arrRoadAddrArg[4] = arrAddrInfo[15];	// 산여부
			arrRoadAddrArg[5] = arrAddrInfo[22];	// 주번지
			arrRoadAddrArg[6] = arrAddrInfo[23];	// 부번지
			arrRoadAddrArg[0] = "";					// PNU 코드
			arrRoadAddrArg[1] = arrAddrInfo[8];		// 건물관리번호
			arrRoadAddrArg[2] = arrAddrInfo[1];		// 상세주소
			arrRoadAddrArg[3] = "";					// 부가주소
			arrRoadAddrArg[9] = "1";				// ARCONVPGM
			arrRoadAddrArg[10] = "01";				// ARCONVSTATUS
		}
	}
}

function edKeyWord_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13) {	// Enter key
		lfn_Search();
	}
}

// 초기화
function btnCancel_OnClick(obj)
{
	ds_oAgentList.Clear();
	radAgent.Value = "1";
	lfn_Cancel(obj);
	edKeyWord.SetFocus();
}

function gdAgentList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (IsExistVar("sIsPopup")) {   // sKeyword , sGubun 인자값
        var arrArg = Array(2);
        arrArg[0] = ds_oAgentList.GetColumn(ds_oAgentList.currow,  "AGMGNO");		        // 구분
        arrArg[1] = ds_oAgentList.GetColumn(ds_oAgentList.currow,  "AGNM");		        // 대상물관리번호 
        
        Close(arrArg);
    }
}

function radAgent_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	edKeyWord.Text = "";
	edKeyWord.SetFocus();
}
