var sGubun = "";
/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    // 공통 코드 조회    
    gfn_SearchJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회
	//gfn_SearchCustomerCourse(ds_oCourse, "Y", ""); // 대표고객직능 조회
	gfn_SearchCustomerCourseFullName(ds_ioCourse, "0", "선택");	// 대표고객직능 조회
	radCourse.Value = "";

	//콤보박스-지역
	cbxJibu_OnChanged("",cbxJibu.Value);//지역 코드 조회
    cbxRegion1.Index = 0;
    cbxRegion2.index = ds_oRegion.rowcount-1;

    cbxCourse.index=0;
	cbxCourse1.index=0;      
	cbxCourse2.index=0;
	cbxCourse3.index=0;
	cbxCourse4.index=0;
	cbxCourse5.index=0;
	cbxCourse6.index=0; 

	calStartDate.Value = left(GetDate(), 8);
    calEndDate.Value = left(GetDate(), 8);
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdManagerList.Width = nCx - gdManagerList.Left - 8;
        Shape1.Width = nCx - Shape1.Left - 4;
    }
}

/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    btnToExcel.Enable = bStatus; // 엑셀 버튼
    btnSave.Enable = bStatus; // 수정&저장 버튼
}

/*
*콤보박스 변경 이벤트 시작
*/
//지부
function cbxJibu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{ 
//    ds_oRegion.ClearData();
	gfn_SearchCodeRegion(strCode, ds_oRegion, "0");	//지역 코드 조회
    cbxRegion1.Index = 0;
    cbxRegion2.index = ds_oRegion.rowcount-1;
}

//지역--라디오 버튼 선택시
function radRegion_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if (radRegion.Value == "1") { // 선택 
        cbxRegion1.Enable =  true;
        cbxRegion2.Enable =  true;                       
        cbxRegion1.SetFocus();
        cbxRegion1.Index = 0;
        cbxRegion2.index = ds_oRegion.rowcount-1;    
	} else {              // 전체
        cbxRegion1.Enable =  false;
        cbxRegion2.Enable =  false;                
        cbxRegion1.Index = 0;
        cbxRegion2.index = ds_oRegion.rowcount-1;    
	}
}

// 직능
function radCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 1) {          //선택
        cbxCourse.SetFocus();
        cbxCourse.Enable = true;
        cbxCourse1.Enable = false;
        cbxCourse2.Enable = false;
        cbxCourse3.Enable = false;
        cbxCourse4.Enable = false;
        cbxCourse5.Enable = false;
        cbxCourse6.Enable = false;        
        cbxCourse.index=0;
        cbxCourse1.index=0;
        cbxCourse2.index=0;
        cbxCourse3.index=0;
        cbxCourse4.index=0;
        cbxCourse5.index=0;
        cbxCourse6.index=0;
	} else if(strCode == 2){     //다중선택
        cbxCourse1.SetFocus();
        cbxCourse.Enable = false;
        cbxCourse1.Enable = true;
        cbxCourse2.Enable = true;
        cbxCourse3.Enable = true;
        cbxCourse4.Enable = true;
        cbxCourse5.Enable = true;
        cbxCourse6.Enable = true;        
        cbxCourse.index=0;
        cbxCourse1.index=0;
        cbxCourse2.index=0;
        cbxCourse3.index=0;
        cbxCourse4.index=0;
        cbxCourse5.index=0;
        cbxCourse6.index=0;
	} else {                    //전체
        cbxCourse.Enable = false;
        cbxCourse1.Enable = false;
        cbxCourse2.Enable = false;
        cbxCourse3.Enable = false;
        cbxCourse4.Enable = false;
        cbxCourse5.Enable = false;
        cbxCourse6.Enable = false;
        cbxCourse.index=0;
        cbxCourse1.index=0;
        cbxCourse2.index=0;
        cbxCourse3.index=0;
        cbxCourse4.index=0;
        cbxCourse5.index=0;
        cbxCourse6.index=0;
	}
}

// 조건라디오버튼 변경이벤트
function radGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 0) {
        lbDate.Visible = true;
        lbMgnoS.Visible = true;
        calStartDate.Visible = true;
        calEndDate.Visible = true;
        lbMgno.Visible = false;
        lbCustNoS.Visible = false;
        medCustNo1.Visible = false;
        medCustNo2.Visible = false;
        calStartDate.Value = left(GetDate(), 8);
        calEndDate.Value = left(GetDate(), 8);
	} else {
        lbDate.Visible = false;
        lbMgnoS.Visible = false;
        calStartDate.Visible = false;
        calEndDate.Visible = false;
        lbMgno.Visible = true;
        lbCustNoS.Visible = true;
        medCustNo1.Visible = true;
        medCustNo2.Visible = true;
        medCustNo1.Value = "000001";
        medCustNo2.Value = "999999";
	}
}

// 초기화
function btnCancel_OnClick(obj)
{
    radMemberGubun.Value = "0";
    radGubun.Value = "0";    
    ds_oManagerList.Clear();
	lfn_Cancel(obj);
	radRegion_OnChanged(Object("radRegion"), radRegion.Value);
	radCourse_OnChanged(Object("radCourse"), radCourse.Value);
	radGubun_OnChanged(Object("radGubun"), radGubun.Value);
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {       
    // 조회이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",지역=" + quote(cbxRegion1.Text) + "~" + quote(cbxRegion2.Text);
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );

    // 직능 다중선택
    var COURSECD = "";
    if (radCourse.Value == 2) {
        for ( var i=1; i < 7; i++ ) {
            if (!gfn_IsNull(object("cbxCourse" + toString(i)).Value)) {
                COURSECD += left(object("cbxCourse" + toString(i)).Value, 1) + ",";
            }
        }        
    }    
    COURSECD = left(COURSECD, length(COURSECD)-1);
    medTotalCnt.Value = "";
    if (radMemberGubun.Value == "0") {
        sGubun = "0";
    } else {
        sGubun = "1";
    }
    ds_oManagerList.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust0060BatchChange", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oManagerList=ds_oManagerList"	// outDs
		,"CSTATUSGUBUN=" + quote(radMemberGubun.Value)
          + " CGTMGNO=" + quote(cbxJibu.Value)
          + " CREGCD1=" + quote(cbxRegion1.Value)
          + " CREGCD2=" + quote(cbxRegion2.Value)
          + " CCOURSECD=" + quote(left(cbxCourse.Value, 1))          
          + " CNO1=" + quote(medCustNo1.Value)
          + " CNO2=" + quote(medCustNo2.Value)
          + " CREGISTERYYMM1=" + quote(left(calStartDate.Value, 8))
          + " CREGISTERYYMM2=" + quote(left(calEndDate.Value, 8))
          + " REGIONGUBUN=" + quote(radRegion.Value)
          + " COURSEGUBUN=" + quote(radCourse.Value)
          + " GUBUN=" + quote(radGubun.Value)
          + " CCOURSECDSELECT=" + quote(COURSECD)// args 
		,"lfn_AfterSearchManagerList"
		,false
	);
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {
    var cnt = 0;
    for (var i=0;i<ds_oManagerList.GetCount();i++) {
        if (ds_oManagerList.GetColumn(i, "SEL") == "1") {
            cnt += 1;
        }
    }
    if (cnt == 0) {
        gfn_Alert("변경할 데이터가 없습니다.");
        return;
    }

    if (!gfn_Confirm("일괄 변경처리 하시겠습니까?")) { 
        return false;
    }

    tit_clearActionInfo();
    if (sGubun == "0") {
        tit_addMultiActionInfo("cust:executeCust0060CustStatusToIrregular", "", 0, "", "N");  //정상에서 비정상으로
    } else {
        tit_addMultiActionInfo("cust:executeCust0060CustStatusToRegularity", "", 0, "", "N");  //비정상에서 정상으로
    }    
    tit_addMultiActionInfo("cust:executeCust0010UpdateCustomerChangeH", "", 0, "", "N");  //고객상태변경이력
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,"ds_iManagerList=ds_oManagerList:U ds_iCustomerInfoTmp=ds_oManagerList:U"	// inDs
        ,"ds_oOverBizProc=ds_oOverBizProc"	// outDs
        ,""	// args 
        ,"lfn_AfterSaveAgentInfo"
        ,false
    );
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 교육일정관리 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchManagerList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    //trace(ds_oManagerList.SaveXML());

    medTotalCnt.Value = ds_oManagerList.rowcount;
    gfn_SearchMessage(gdManagerList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

function lfn_AfterSaveAgentInfo(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    lfn_Search(); // 교육일정관리 조회
}



