function lfn_Init(obj)
{
	gfn_FormInit(obj);
	radReport.Index = 0;														// 출력물
	radReturnGubun.Index = 0;													// 반송구분
	gfn_SearchCustomerCourseFullName(ds_oCourse, "0", "선택");					// 대표고객직능 조회
    gfn_SearchAllJibu(cbxJibu, "Y"); 											// 지부 코드 조회
    gfn_SearchCommonCode(ds_oBusinessType, "BIZCD", "Y", "선택"); 				// 방관-용도코드
	cbxJibu_OnChanged(Object("cbxJibu"),cbxJibu.Value);							// 지부-소속지부
	radRegion_OnChanged(Object("radRegion"),radRegion.Value);					// 지역-전체
	radCourse_OnChanged(Object("radCourse"),radCourse.Value);					// 직능-전체
	radBusinessType_OnChanged(Object("radCourse"),radBusinessType.Value);		// 업종-전체
}

// 직능 선택
function radCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_SelectCourse(strCode);
}

// 직능 선택
function lfn_SelectCourse(strCode) {
	if(gfn_IsNull(strCode)) {	//전체
		cbxCourse.Index = 0;
		cbxCourse1.Index = 0;
		cbxCourse2.Index = 0;
		cbxCourse3.Index = 0;
		cbxCourse4.Index = 0;
		cbxCourse5.Index = 0;
		cbxCourse6.Index = 0;
		cbxCourse.Enable = false;
		cbxCourse1.Enable = false;
		cbxCourse2.Enable = false;
		cbxCourse3.Enable = false;
		cbxCourse4.Enable = false;
		cbxCourse5.Enable = false;
		cbxCourse6.Enable = false;
	} else if(strCode == 0) {	//선택
		cbxCourse.Index = 0;
		cbxCourse1.Index = 0;
		cbxCourse2.Index = 0;
		cbxCourse3.Index = 0;
		cbxCourse4.Index = 0;
		cbxCourse5.Index = 0;
		cbxCourse6.Index = 0;
		cbxCourse.Enable = true;
		cbxCourse1.Enable = false;
		cbxCourse2.Enable = false;
		cbxCourse3.Enable = false;
		cbxCourse4.Enable = false;
		cbxCourse5.Enable = false;
		cbxCourse6.Enable = false;
	} else if(strCode == 1) {	//다중선택
		cbxCourse.Index = 0;
		cbxCourse1.Index = 0;
		cbxCourse2.Index = 0;
		cbxCourse3.Index = 0;
		cbxCourse4.Index = 0;
		cbxCourse5.Index = 0;
		cbxCourse6.Index = 0;
		cbxCourse.Enable = false;
		cbxCourse1.Enable = true;
		cbxCourse2.Enable = true;
		cbxCourse3.Enable = true;
		cbxCourse4.Enable = true;
		cbxCourse5.Enable = true;
		cbxCourse6.Enable = true;
	}
}

