// 지부변경
function lfn_JibuChanged(strCode) {
	gfn_SearchCodeRegion(strCode, ds_oRegion, "0");	//지역 코드 조회

	if(radRegion.Value == "Y") {
		cbxRegion1.Index = 0;
		cbxRegion2.Index = ds_oRegion.rowcount - 1;
	}
}

// 지역변경
function radRegion_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(gfn_IsNull(strCode)) {
		lfn_RegionChanged(false);
	} else {
		lfn_RegionChanged(true);
	}
}

// 지역변경
function lfn_RegionChanged(bStatus) {
	cbxRegion1.Enable = bStatus;
	cbxRegion2.Enable = bStatus;

	if(bStatus == false) {
		cbxRegion1.Value = "";
		cbxRegion2.Value = "";
	} else {
		cbxRegion1.Index = 0;
		cbxRegion2.Index = ds_oRegion.rowcount - 1;
	}
}

// 업종변경
