	} else {
		lfn_BusinessTypeChanged(true);
	}
}

// 업종변경
function lfn_BusinessTypeChanged(bStatus) {
	cbxBusinessType1.Enable = bStatus;
	cbxBusinessType2.Enable = bStatus;

	if(bStatus == false) {
		cbxBusinessType1.Value = "";
		cbxBusinessType2.Value = "";
	} else {
