{
	if(gfn_IsNull(strCode)) {
		lfn_DateChanged(false);
	} else {
		lfn_DateChanged(true);
	}
}

// 일자변경
function lfn_DateChanged(bStatus) {
	calStartDate.Enable = bStatus;
	calEndDate.Enable = bStatus;

	if(bStatus == false) {
		calStartDate.Value = "";
		calEndDate.Value = "";
	} else {
		calStartDate.Value = left(GetDate(),4) + "0101";
		calEndDate.Value = left(GetDate(),4) + "1231";
	}
}


/*
 * 기능 : 조회 (현황출력)
 */
function lfn_Search1(obj)
{
	var lsCourseList = ""; //직능리스트

	if(radCourse.Value == 0) {	//선택
		if(!gfn_IsNull(cbxCourse.Value)) {
			lsCourseList += toString(trim(left(cbxCourse.Value,1)));
		}
	} else if(radCourse.Value == 1) {	//다중선택
        for ( var i=1; i < 7; i++ ) {
            if (!gfn_IsNull(object("cbxCourse" + toString(i)).Value)) {
                lsCourseList += left(object("cbxCourse" + toString(i)).Value, 1) + ",";
            }
        }        
		lsCourseList = left(lsCourseList, length(lsCourseList)-1);
	}
	ds_oCheckList.ClearData();
	ds_oListSum.ClearData();

	tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust0090ReturnPostReport1", false);

	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oCheckList=ds_oCheckList"	// outDs
		,"SJIBU=" + quote(cbxJibu.Value)
		+ " CREGCD1=" + quote(cbxRegion1.Value)
		+ " CREGCD2=" + quote(cbxRegion2.Value)
		+ " SCOURSE=" + quote(lsCourseList)
		+ " SCNO1=" + quote(meCustNo1.Value)
		+ " SCNO2=" + quote(meCustNo2.Value)
		+ " SBBIZCD1=" + quote(cbxBusinessType1.Value)
		+ " SBBIZCD2=" + quote(cbxBusinessType2.Value)
		+ " SPDDATE1=" + quote(calStartDate.Value)
		+ " SPDDATE2=" + quote(calEndDate.Value)
		+ " SBIZGUBUN=" + quote(radReturnGubun.Value)
		,"lfn_AfterSearch"
		,false
		,false
		,true
	);
}

/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : ErrorCode - 에러코드
 *             ErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    gdListSum.UserData = "";
    gdCheckList.UserData = "반송우편리스트";
    lfn_PrintMode();	//출력모드
}

/*
 * 출력모드(workFlag 0:출력, 1:엑셀)
 */
function lfn_PrintMode() {

	if(ds_oCheckList.GetRowCount() > 0 || ds_oListSum.GetRowCount() > 0) {
		switch(workFlag)
		{
			case 0 :	//출력
				if(radReport.Value = 0) {	//현황
					gfn_CallReportPopup("frmCust0090R01ReturnPostReport.rex"
								  , "ds_oCheckList"
								  , "JIBU=" + cbxJibu.Text
								  , true);
				} else {	//집계
					gfn_CallReportPopup("frmCust0090R02ReturnPostReport.rex"
								  , "ds_oSumList"
								  , "JIBU=" + cbxJibu.Text
								  , true);
				}

			break;
			case 1 :	//엑셀
				if(radReport.Value = 0) {	//현황
					// 개인정보 다운로드 사유 등록
					var parValue = "sCode=1409";	// COMMON.PERSONALDOWNLOADH
						parValue += " sRowCnt=" + ds_oCheckList.GetRowCount();
						parValue += " sTitle=" + quote(gdCheckList.UserData);
					var ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

					if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
						return false;
					}
					lfn_Excel();
				} else {	//집계
					lfn_Excel();
				}
			break;
		}
    } else {
		gfn_Alert("출력할 데이터가 없습니다.");
    }
}

/*
 * 집계출력
 */
function lfn_Search2(obj)
{
	var lsCourseList = ""; //직능리스트

	if(radCourse.Value == 0) {	//선택
		if(!gfn_IsNull(cbxCourse.Value)) {
			lsCourseList += toString(trim(left(cbxCourse.Value,1)));
		}
	} else if(radCourse.Value == 1) {	//다중선택
        for ( var i=1; i < 7; i++ ) {
            if (!gfn_IsNull(object("cbxCourse" + toString(i)).Value)) {
                lsCourseList += left(object("cbxCourse" + toString(i)).Value, 1) + ",";
            }
        }        
		lsCourseList = left(lsCourseList, length(lsCourseList)-1);
	}
	ds_oSumList.ClearData();

	tit_clearActionInfo();
	tit_addSearchActionInfo("cust:searchCust0090GetRegionForm", false);
	tit_addSearchActionInfo("cust:searchCust0090ReturnPostReport2", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oRegionForm=ds_oRegionForm ds_oSumList=ds_oSumList"	// outDs
		,"SJIBU=" + quote(cbxJibu.Value)
		+ " CREGCD1=" + quote(cbxRegion1.Value)
		+ " CREGCD2=" + quote(cbxRegion2.Value)
		+ " SCOURSE=" + quote(lsCourseList)
		+ " SCNO1=" + quote(meCustNo1.Value)
		+ " SCNO2=" + quote(meCustNo2.Value)
		+ " SBBIZCD1=" + quote(cbxBusinessType1.Value)
		+ " SBBIZCD2=" + quote(cbxBusinessType2.Value)
		+ " SPDDATE1=" + quote(calStartDate.Value)
		+ " SPDDATE2=" + quote(calEndDate.Value)
		+ " SBIZGUBUN=" + quote(radReturnGubun.Value)
		,"lfn_AfterDataConversion"
		,false
		,false
		,true
	);
}


/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : ErrorCode - 에러코드
 *             ErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterDataConversion(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
	gdListSum.UserData = "우편반송현황집계";
    gdCheckList.UserData = "";
    lfn_DataConversion();	//통계데이터 변환
}

// 통계데이터 변환
function lfn_DataConversion() {
	//ds_oListSum -> strDataset	(타겟)
	//ds_oSumList -> objDataset (소스)

	ds_oListSum.Clear();

	ds_oListSum.InsertRow(0);
	ds_oListSum.AddColumn("JIBU");	//지부 컬럼

	for(var i=0; i<ds_oRegionForm.rowcount; i++) {
		ds_oListSum.AddColumn(ds_oRegionForm.GetColumn(i, "CD"));	//각지역 컬럼
	}
	ds_oListSum.AddColumn("TOTAL");	//전체 합계 컬럼

	var nRow = ds_oListSum.AddRow();
	var sTotal = 0;	//전체 합계

	for(var i=0; i<ds_oListSum.colcount; i++) {	//0으로 초기화
		ds_oListSum.SetColumn(nRow, i, 0);
	}

	ds_oListSum.SetColumn(nRow, "JIBU", ds_oSumList.GetColumn(0, "GTTEAMNM"));	//지부 셋팅

	for(var i=0; i<ds_oListSum.colcount; i++) {
		for(var j=0; j<ds_oSumList.rowcount; j++) {
			if(ds_oListSum.GetColID(i) == ds_oSumList.GetColumn(j, "CREGCD")) {
				ds_oListSum.SetColumn(0, ds_oListSum.GetColID(i), ds_oSumList.GetColumn(j, "CNT"));
				sTotal += toNumber(ds_oSumList.GetColumn(j, "CNT"));
			}
		}
	}

	ds_oListSum.SetColumn(nRow, "TOTAL", sTotal);

	//그리드 셋팅
	gdListSum.BindDataset = "ds_oListSum";
	gdListSum.CreateFormat();
	var pos_no = pos(gdListSum.Contents, '</body>') + 7;
	var str_contents = left(gdListSum.Contents, pos_no);
		str_contents += '<summary>';
	for(var i=0; i<ds_oListSum.GetColCount(); i++)
	{
		str_contents += '<cell col=' + i + ' display="text"/>';
	}
	gdListSum.contents = str_contents +'</summary></contents>';

	// 그리드 Summary 편집
	gdListSum.SetCellProp("summary", 0, "text", "총계");
	for(var i=0; i<gdListSum.GetColCount(); i++) {
		//expr="sum(&apos;01&apos;)"
		for(var j=0; j<ds_oRegionForm.rowcount; j++) {
			if(gdListSum.GetCellProp("head", i, "text") == ds_oRegionForm.GetColumn(j, "CD")) {
				gdListSum.SetCellProp("summary", i, "expr", "sum('"+ds_oRegionForm.GetColumn(j, 'CD')+"')");
			}
		}
	}
	gdListSum.SetCellProp("summary", gdListSum.GetColCount()-1, "expr", "sum('TOTAL')");

	// 그리드 Head 편집
	gdListSum.SetCellProp("head", 0, "text", "지역");
	for(var i=0; i<gdListSum.GetColCount(); i++) {

		for(var j=0; j<ds_oRegionForm.rowcount; j++) {
			if(gdListSum.GetCellProp("head", i, "text") == ds_oRegionForm.GetColumn(j, "CD")) {
				gdListSum.SetCellProp("head", i, "text", ds_oRegionForm.GetColumn(j, "DATA"));
			}
		}
	}
	gdListSum.SetCellProp("head", gdListSum.GetColCount()-1, "text", "소계");

	lfn_PrintMode();
}
