/직능
function radCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 1) {          //선택
        cbxCourse.SetFocus();
        cbxCourse.Enable = true;
        cbxCourse1.Enable = false;
        cbxCourse2.Enable = false;
        cbxCourse3.Enable = false;
        cbxCourse4.Enable = false;
        cbxCourse5.Enable = false;
        cbxCourse6.Enable = false;        
        cbxCourse.index=0;
        cbxCourse_OnChanged(object("cbxCourse"),"");
        cbxCourse1.index=0;
        cbxCourse2.index=0;
        cbxCourse3.index=0;
        cbxCourse4.index=0;
        cbxCourse5.index=0;
        cbxCourse6.index=0;
        cbxCourseD.Enable = false;	
        cbxCourseD.index = 0;
	} else if(strCode == 2){     //다중선택
        cbxCourse1.SetFocus();
        cbxCourse.Enable = false;
        cbxCourse1.Enable = true;
        cbxCourse2.Enable = true;
        cbxCourse3.Enable = true;
        cbxCourse4.Enable = true;
        cbxCourse5.Enable = true;
        cbxCourse6.Enable = true;        
        cbxCourse.index=0;
        cbxCourse_OnChanged(object("cbxCourse"),"");
        cbxCourse1.index=0;
        cbxCourse2.index=0;
        cbxCourse3.index=0;
        cbxCourse4.index=0;
        cbxCourse5.index=0;
        cbxCourse6.index=0;
        radCourseD.Enable = false;
        radCourseD.Index="0";	
        cbxCourseD.Enable = false;
        cbxCourseD.index = 0;       
	} else {                    //전체
        cbxCourse.Enable = false;
        cbxCourse1.Enable = false;
        cbxCourse2.Enable = false;
        cbxCourse3.Enable = false;
        cbxCourse4.Enable = false;
        cbxCourse5.Enable = false;
        cbxCourse6.Enable = false;
        cbxCourse.index=0;
        cbxCourse_OnChanged(object("cbxCourse"),"");
        cbxCourse1.index=0;
        cbxCourse2.index=0;
        cbxCourse3.index=0;
        cbxCourse4.index=0;
        cbxCourse5.index=0;
        cbxCourse6.index=0;
        radCourseD.Enable = false;
        radCourseD.Index="0";
        cbxCourseD.Enable = false;
        cbxCourseD.index = 0;    
	}
}


//용도
function radBusinessType_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "1") { // 선택 
		cbxBusinessType1.Index = "0"; 
		cbxBusinessType2.Index = ds_oAdditionInfo.rowcount - 1;
        cbxBusinessType1.Enable = true;
        cbxBusinessType2.Enable = true;                       
        cbxBusinessType1.SetFocus();
	} else {
        cbxBusinessType1.Index = "0"; 
		cbxBusinessType2.Index = ds_oAdditionInfo.rowcount - 1;
        cbxBusinessType1.Enable = false;
        cbxBusinessType2.Enable = false;                       	
	}
}

function radMgno_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode == "1"){
		medMgno1.SetFocus();
		medMgno1.Enable = true;
		medMgno2.Enable = true;
	}else{
		medMgno1.Text = "";
		medMgno2.Text = "";
		medMgno1.Enable = false;
		medMgno2.Enable = false;
	}
}

function radRegion_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "1") {          //선택
        cbxRegion1.SetFocus();
        cbxRegion1.Index = 0;
		cbxRegion2.Index = ds_oRegion.rowcount - 1;
        cbxRegion1.Enable = true;
        cbxRegion2.Enable = true;
    }else {
		cbxRegion1.Index = 0;
		cbxRegion2.Index = ds_oRegion.rowcount - 1;
		cbxRegion1.Enable = false;
        cbxRegion2.Enable = false;
    }    
}

function lfn_Cancel(obj)
{
	radReport.Value = 1;
	radAddrGubun.Value = 1;

	radBusinessType_OnChanged();
	radRegion_OnChanged();
	radMgno_OnChanged();
	radCourse_OnChanged();
	radCourseD_OnChanged();
}

//대표직능 --> 선택콤보
function cbxCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if(gfn_IsNull(strCode)){		
        radCourseD.Enable = false;
        radCourseD.Index="0";
        cbxCourseD.Enable = false;
        cbxCourseD1.Enable = false;
        cbxCourseD2.Enable = false;
        cbxCourseD3.Enable = false;
        cbxCourseD4.Enable = false;
        cbxCourseD5.Enable = false;
        cbxCourseD6.Enable = false;   
    }else{
        gfn_SearchCustomerDetailCourse(cbxCourse.Value,ds_ioCourseD, "0", "선택");// 세부고객직능 조회	
        radCourseD.Enable = true;
        radCourseD.Index="0";
        cbxCourseD.Enable = false;
        cbxCourseD1.Enable = false;
        cbxCourseD2.Enable = false;
        cbxCourseD3.Enable = false;
        cbxCourseD4.Enable = false;
        cbxCourseD5.Enable = false;
        cbxCourseD6.Enable = false; 
        cbxCourseD.index = 0;
        cbxCourseD1.index = 0;
        cbxCourseD2.index = 0;
        cbxCourseD3.index = 0;
        cbxCourseD4.index = 0;
        cbxCourseD5.index = 0;
        cbxCourseD6.index = 0;

    }
}


