#include "LIB::cust.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    // 공통 코드 조회
    gfn_SearchJibu(cbxJibu, "Y", "선택"); // 지부 코드 조회
}

function Form_OnKeyDown(obj,objJubsuerObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objJubsuerObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState) {
    if (gs_IsResizingForm == "Y") {
        gdRegionManager.Height = nCy - gdRegionManager.Top - 4;
        gdRegionManager.Width = nCx - gdRegionManager.Left - 4;
    }
}

/**
 * 엑셀 출력
 */
function lfn_Excel(obj) {
    if (ds_ioRegionManager.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {

    var ls_SearchGubun = "";

    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }

    ds_ioRegionManager.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust0190RegionManager", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioRegionManager=ds_oRegionManager"	// outDs
		,"RCGTMGNO=" + quote(cbxJibu.Value) // args 
		,"lfn_AfterSearch"
		,false

	);
	//alert(ds_ioRegionManager.GetRowCount());
}
function lfn_AfterSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
	medTotalCnt.Value = ds_ioRegionManager.GetRowCount();
    gfn_SearchMessage(gdRegionManager); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 사용자 함수 */

/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
	btnSave.Enable = bStatus; // 수정&저장 버튼
}

/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj) {
	gfn_GridMultiSort(gdRegionManager, 0); // 멀티 정렬을 실행한다.
}



function gdRegionManager_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {

    if (nCell == 2) {
        ds_ioRegionManager.row = nRow;
        gdRegionManager_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex);
    }
    //gdRegionManager_OnCellClick
}



function gdRegionManager_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    if (nCell == 2) {
        var arrRegionManagerInfo = Dialog("sys::frmsys0021PUserList.xml", "", 600, 400, "", -1, -1);
		if (length(arrRegionManagerInfo) > 0) {
	
				ds_ioRegionManager.SetColumn(nRow, "RMPSABUN", arrRegionManagerInfo[4]); //담당자사번
				
				ds_ioRegionManager.SetColumn(nRow, "PNM", arrRegionManagerInfo[5]); //담당자성명
				
		}




//        if (arrRegionManagerInfo) {
            //lfn_Search();
//        }
    }
}


function lfn_Save(obj)
{
	if (ds_ioRegionManager.GetCount() == 0) {
	
		gfn_Alert("변경할 내역이 없습니다.");
		return;
	
	}
	
	if(!gfn_Confirm("변경내역을 저장하시겠습니까?")){
		return;
	}
	
	// dataset  
    for(var i=0; i<ds_ioRegionManager.GetRowCount(); i++) {

		if (!gfn_IsNull(ds_ioRegionManager.GetColumn(i, "PNM"))) {

			tit_clearActionInfo();
			
			iArgsNm = "RMGTMGNO=" + quote(ds_ioRegionManager.GetColumn(i, "RCGTMGNO"))
			          + " RMREGCD=" + quote(ds_ioRegionManager.GetColumn(i, "RCREGCD"))
			          + " RMPSABUN=" + quote(ds_ioRegionManager.GetColumn(i, "RMPSABUN"));
			
			tit_addSingleActionInfo("cust:updateCust0190RegionManager", "", 0, "");  // 저장
			
			// 서버 호출 
			tit_callService(
				""
				,""
				,""		// inDs
				,""      // outDs
				,iArgsNm	// args
				,"lfn_AfterUpdate"
				,""
				,""
				,true
			);			
		
		}
	} // for
	
	lfn_Search();
}


function lfn_AfterUpdate(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
	
}

function lfn_Cancel(obj)
{
	ds_ioRegionManager.Clear();
}
