#include "LIB::cust.js"; // 공통업무 관련
var vErrGubun = "N";
/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    // 공통 코드 조회
    gfn_SearchJibu(cbxJibu, "Y", "선택"); // 지부 코드 조회
}

function Form_OnKeyDown(obj,objJubsuerObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objJubsuerObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState) {
    if (gs_IsResizingForm == "Y") {
        gdEduJubsuNumManager.Height = nCy - gdEduJubsuNumManager.Top - 4;
        gdEduJubsuNumManager.Width = nCx - gdEduJubsuNumManager.Left - 4;
    }
}

/**
 * 엑셀 출력
 */
function lfn_Excel(obj) {
    if (ds_ioEduJubsuNum.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {

    var ls_SearchGubun = "";

    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }

    ds_ioEduJubsuNum.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust1022EduJubsuNum", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioEduJubsuNum=ds_oEduJubsuNum"	// outDs
		,"GTMGNO=" + quote(cbxJibu.Value) // args 
		,"lfn_AfterSearch"
		,false

	);
}
function lfn_AfterSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    gfn_SearchMessage(gdEduJubsuNumManager); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 사용자 함수 */

/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
	btnSave.Enable = bStatus; // 수정&저장 버튼
}

/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj) {
	gfn_GridMultiSort(gdEduJubsuNumManager, 0); // 멀티 정렬을 실행한다.
}
function lfn_Save(obj)
{
	if (ds_ioEduJubsuNum.GetCount() == 0) {
	
		gfn_Alert("변경할 내역이 없습니다.");
		return;
	
	}
	
	if(!gfn_Confirm("변경내역을 저장하시겠습니까?")){
		return;
	}
	
	// dataset  
    for(var i=0; i<ds_ioEduJubsuNum.GetRowCount(); i++) {

		if (!gfn_IsNull(ds_ioEduJubsuNum.GetColumn(i, "ELCOURSECD"))) {

			if (ds_ioEduJubsuNum.GetOrgColumn(i, "ELJUBSUMAXCNT") <> ds_ioEduJubsuNum.GetColumn(i, "ELJUBSUMAXCNT")) {

				tit_clearActionInfo();
				
				iArgsNm = "ELGTMGNO=" + quote(ds_ioEduJubsuNum.GetColumn(i, "ELGTMGNO"))
						  + " ELWORKGUBUN=" + quote(ds_ioEduJubsuNum.GetColumn(i, "ELWORKGUBUN"))
						  + " ELCOURSECD=" + quote(ds_ioEduJubsuNum.GetColumn(i, "ELCOURSECD"))
						  + " ELJUBSUMAXCNT=" + quote(ds_ioEduJubsuNum.GetColumn(i, "ELJUBSUMAXCNT"));
				
				tit_addSingleActionInfo("cust:updateCust1022EduJubsuNum", "", 0, "");  // 저장
				
				// 서버 호출 
				tit_callService(
					""
					,""
					,""		// inDs
					,""      // outDs
					,iArgsNm	// args
					,"lfn_AfterUpdate"
					,""
					,""
					,true
				);			
			}
		}
	} // for
	
	if (vErrGubun == "N") {
	
		gfn_Alert("변경내역이 정상적으로 저장되었습니다.");
		
	}
	lfn_Search();
}


function lfn_AfterUpdate(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
		vErrGubun = "Y";	// 에러여부
		
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    } else {
    
		vErrGubun = "N";	// 에러여부
    
    }
	
}

function lfn_Cancel(obj)
{
	ds_ioEduJubsuNum.Clear();
}
