#include "LIB::cust.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

	// 공통 코드 조회
    gfn_SearchAllJibu(cbxJibu, "Y", "전체"); // 지부코드 조회

    if (gds_oUserInfo.GetColumn(0, "ADMINGUBUN") <> "SM") { // 관리자가 아닐 경우
		cbxJibu.Enable = false;
    }

    gfn_SearchCustomerCourseFullName(ds_oCourse, "0", ""); // 대표직능코드 조회

    for (var i=(ds_oCourse.GetRowCount()-1); i>-1; i--) {
		if (ds_oCourse.GetColumn(i, "CD") == "60" || ds_oCourse.GetColumn(i, "CD") == "70") { // 개인회원, 단체회원 삭제
			ds_oCourse.DeleteRow(i);
		}
	}

    gfn_SearchCustomerDetailCourseFullName("", ds_oDetailCourse, "0", "");	// 세부직능코드 조회
    gfn_SearchRegion("", ds_oRegion, "Y", "선택"); //지역코드 조회

    if (IsExistVar("sJibuCode")) {
	    cbxJibu.Value = sJibuCode;
    }

    if (IsExistVar("sCustNo")) {
	    edCustNo.text = sCustNo;
	}

    if (IsExistVar("sCustStatus")) {
        cbxStatus.Value = sCustStatus;
    }

    if (IsExistVar("sCourseCd")) {
        edCourseCd.Text = left(sCourseCd, 1);
    }

    edCustNo.SetFocus();
    lfn_Search(); // 조회
}

/*
 * 기능 : Form이 종료되거나 화면 전환시
 */
function Form_OnUnloadCompleted(obj) {
    Close(); // 종료
}

