/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    if (length(edCustNo.Text) < 3) {
		gfn_Alert("고객번호 3자리 이상 입력하고 조회하세요.");
		edCustNo.SetFocus();
		return false;
    }

    ds_oCustUser.ClearData(); // 데이타셋 초기화
	tit_clearActionInfo();

	tit_addSearchActionInfo("cust:searchCust1092PopUpCustomerList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oCustUser=ds_oCustData" // outDs
		,"JIBUCD=" + quote(cbxJibu.Value) //-- 지부
			+" CUSTNO=" + quote(edCustNo.Text) //-- 고객번호
			+" STATUS=" + quote(cbxStatus.Value) //-- 회원상태
			+" COURSECD=" + quote(edCourseCd.Text) //-- 직능 // args 
		,"lfn_AfterSearchPopUpCustomerList"
		,false
		,false
		,true
	);
}

/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchPopUpCustomerList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

	// if (ds_oCustUser.GetRowCount() == 1) {
        // btnSelect_OnClick();
    // }

    gfn_SearchMessage(gdCustUser); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close(); // 종료
}

/* 공통 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

/*
 * 기능 : 그리드 멀티정렬
 */
function btnMutilSort_OnClick(obj) {
	gfn_GridMultiSort(gdCustUser, 0); // 멀티 정렬을 실행한다.
}

/*
 * 기능 : 선택
 */
function btnSelect_OnClick(obj) {
    if (ds_oCustUser.row == -1) {
        Close();
    }

    var arrArg = Array();
    arrArg[0] = ds_oCustUser.GetColumn(ds_oCustUser.row, "CMGNO"); // 고객바코드
    arrArg[1] = ds_oCustUser.GetColumn(ds_oCustUser.row, "CGTMGNO"); // 지부
    arrArg[2] = ds_oCustUser.GetColumn(ds_oCustUser.row, "CNO"); // 고객번호
    Close(arrArg);
}

/*
 * 기능 : Head 영역의 Cell을 Click
 */
function gdCustUser_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

/*
 * 기능 : 키다운
 */
function edCustNo_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
	if (nChar == 13) {
        lfn_Search(); // 조회
	}
}

/*
 * 기능 : Body 영역의 Cell을 Double Click 
 */
function gdCustUser_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	btnSelect_OnClick(obj);
}

/*
 * 기능 : 고객상태 콤보값 변경
 */
function cbxStatus_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	lfn_Search(); // 조회
}

/*
 * 기능 : 지부 콤보값 변경
 */
function cbxJibu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	lfn_Search(); // 조회
}
