   ds_oFireCd.Copy("gds_oFireCode");  				 	//소방서 콤보세팅	
   ds_oFireCenterCd.Copy("gds_oFireCenterCode");		//소방센터 콤보세팅
   ds_oFireCenterCd2.Copy("gds_oFireCenterCode");		//소방센터 콤보세팅
   
	// 경기 북부는 경기로 .. 나머진 그대
	var PDEPTCD = gds_oUserInfo.GetColumn(0, "PDEPTCD");
	var chkPDEPTCD = "";      // 경기북부 경우 체크 
//	if (PDEPTCD == "1141") {
//		chkPDEPTCD = "1081";
//	} else {
		chkPDEPTCD = PDEPTCD;
//	}
   
	if(ds_oBonbu.FindRow("JIBUCD", chkPDEPTCD) <> -1) {	// 해당본부 세팅
		ds_oBonbu.Filter("JIBUCD="+quote(chkPDEPTCD));
	} 
 
   //lfn_FilterFireStation(substr(cbxBonbu.Value,0,2));	// 소방서 필터링

   CGROUPCD_A = "30";		// 위험물

   lfn_SearchCourseDetail(); // 세부고객직능 조회
   
   radRegin.Index = 1;
   //cbxRegion1.value = ds_oRegion.GetColumn(0, "CD");                          // 지역 첫번째 콤보    
   cbxRegion2.value = "";                   							      // 지역 두번째 콤보 
   cbxRegion3.value = "";                       							  // 지역 세번째 콤보

   cbxRegion1.Enable = true;                                                  // 지역 첫번째 콤보 
   cbxRegion2.Enable = false;                                                 // 지역 두번째 콤보 
