}

/*
 *  키값으로 교육통지대상리스트 상세 조회 
*/
function lfn_Serch_EducationList()
{
    ds_ioMEduNoticeDetail.ClearData(); // 데이터셋 초기화        	 
    tit_clearActionInfo();
    tit_AddCSVSearchActionInfo("cust:searchCust1100MEduNoticeDetail", false);        

    tit_callService(
        ""
        ,""
        ,""// inDs
		,"ds_ioMEduNoticeDetail=ds_oMEduNoticeDetail"  // outDs
        ,"fkeyA=" + quote(ds_ioMEduNotice.GetColumn(0, "fkeyA"))
        + " edCustYear=" + quote(edCustYear.Text)  
          //args                                         
        ,"lfn_AfterSearchElecAppr_aa"
        ,false
        ,""
        ,true

    );
}
function lfn_AfterSearchElecAppr_aa(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    edEduTotalCount.Text = ds_ioMEduNoticeDetail.count;
    edEduCount.Text = ds_ioMEduNoticeDetail.count;
    edEduDay.SetFocus();

    alert("조회가 완료 되었습니다.");

};

