/*
 * 지역 전체 , 선택 변경
*/
function radRegion_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode == "0") {	// 전체
        cbxRegion1.Enable = false;	
        cbxRegion2.Enable = false;
        cbxRegion3.Enable = false;
        cbxRegion1.value = "";	        
        cbxRegion2.value = "";
        cbxRegion3.value = "";
        radCenter.Enable = false;
	} else if(strCode == "1"){	//선택
        cbxRegion1.Enable = true;	
        cbxRegion2.Enable = false;
        cbxRegion3.Enable = false;
        cbxRegion1.value = ds_oRegion.GetColumn(0, "cd");
        cbxRegion2.value = "";
        cbxRegion3.value = "";
        if(radGijun.value == "1") {	// 지부이면
			radCenter.Enable = false;
		} else if(radGijun.value == "2") {	//소방본부이면
			radCenter.Enable = true;
		}
	} else if(strCode == "2") {	// 범위선택
        cbxRegion1.Enable = false;
	    cbxRegion2.Enable = true;
        cbxRegion3.Enable = true;
        cbxRegion1.value = "";
        cbxRegion2.value = ds_oRegion.GetColumn(0, "cd");
        cbxRegion3.value = ds_oRegion.GetColumn(0, "cd");
        radCenter.Enable = false;
	}
    radCenter.Index = 0;
	cbxMFCenter1.Enable = false;
	cbxMFCenter2.Enable = false;
	cbxMFCenter3.Enable = false;
	cbxMFCenter1.value = "";
	cbxMFCenter2.value = "";
	cbxMFCenter3.value = "";

}

