/*
 *  교육 대상자 배정
*/
function btnSetlmt_OnClick(obj)  
{
	if( ds_ioMEduNotice.count ==0 || (gfn_isNull(edEduCount.text) && edEduCount.text ==0 )) {
        alert("추출 대상자수가 없습니다.");
        return false;
	}

	if( (gfn_isNull(edEduCount.text) || edEduCount.text ==0 )) {
        alert("예정 교육대상자수가 없습니다.");
        return false;
	}

	if(ToNumber(edEduTotalCount.Text) < ToNumber(edEduCount.Text)) {
        alert("예정 교육대상자수가 추출대상자수보다 더 많습니다.");
        return false;
	}

	if( (gfn_isNull(edEduDay.text) || edEduDay.text ==0 )) {
        alert("예정 교육일수가 없습니다.");
        return false;
	}

	var sArg = "sEduTotalCount= " + quote(edEduTotalCount.text); // 추출대상자수
	    sArg = sArg + " sEduCount= " + quote(edEduCount.text);    // 교육대상자수
	    sArg = sArg + " sEduDay= " + quote(edEduDay.text);            // 예정교육일수
	    sArg = sArg + " sEduCustGubun= " + quote(radMemberGubun.text);            // 고객구분
	    sArg = sArg + " sEduPerson= " + quote(radCourse_1.text);            // 교육대상
	    sArg = sArg + " sCourseCode= " + quote(radCourse_1.Value);            // 교육대상값 
    var arrSeatNo = Dialog("cust::frmcust1105MEduNotice.xml", sArg, 600, 400, "", -1, -1);
}

