/*
* 교육대상 변경에 따른 용도 표기 변경
*/
function radCourse_1_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
  // CGROUPCD_A = "10";
   	if(strCode == 0){          // 세부직능 표기 위해 해당 대표직능 코드를 구함
        CGROUPCD_A = "10";

   	} else if(strCode == 1){
        CGROUPCD_A = "30";

   	} else if(strCode == 2){
        CGROUPCD_A = "50";

   	} else if(strCode == 3){
        CGROUPCD_A = "80";

   	} else if(strCode == 4){
        CGROUPCD_A = "90";

   	}
	
	// 대표직능 소방안전관리자만 용도 표기
	// 소방안전관리자만 강습수료자 1년이내 통지제외 표기
	if(strCode == 0){         
        cbxBusinessType1.Enable = true;
        cbxBusinessType2.Enable = true;

      //cbxBusinessType1.Value = ds_oCourse.GetColumn(1, "CD");
      //cbxBusinessType2.Value = ds_oCourse.GetColumn(1, "CD");

        cbxBusinessType1.Value = ds_oBizCd.GetColumn(0, "CD");     // 용도 1
        cbxBusinessType2.Value = ds_oBizCd.GetColumn(ds_oBizCd.lastrow, "CD");     // 용도 2 

        Static43.Visible = true;
		chkTrainingExcept.Visible = true;
		CalTrainingStart.Visible = true;
		Static44.Visible = true;
		CalTrainingEnd.Visible = true;

	} else{
        cbxBusinessType1.Enable = false;
        cbxBusinessType2.Enable = false;
        cbxBusinessType1.Value = "";
        cbxBusinessType2.Value = "";
        
		Static43.Visible = false;
		chkTrainingExcept.Visible = false;
		CalTrainingStart.Visible = false;
		Static44.Visible = false;
		CalTrainingEnd.Visible = false;	
	}

    lfn_SearchCourseDetail();  //세부직능
    radCourse.Value = "";      //세부직능
    radCourse_OnChanged();     //세부직능

    radCpersonGubun.Enable=false;
    radCpersonGubun.Index=0;
  //  radCpersonGubun.Value = "";
    cbxCpersonGubun1.Value = "";
    cbxCpersonGubun1.Enable=false;

   //radCpersonGubun.Index = 1;
    radCpersonGubun.Enable = true;
    lfn_FilterPersonGubun(CGROUPCD_A);  // 인력 구분을 위해 대표 직능이 변경되면 해당 인력구분으로 ds 변경됨
    radCpersonGubun_OnChanged(object("radCpersonGubun"),radCpersonGubun.Value); // 인력구분에 따라 콤보의 사용 유무를 결정함
                                                                                //     
}


