/*
* 세부직능 변경시
*/
function cbxCourse1_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(gfn_IsNull(strCode)){   // 인력구분 전체, 선택 유무를 결정  세부직능이 null일경우 인력구분을 전체로함 (비활성화함)
        radCpersonGubun.Enable = false;
        radCpersonGubun.Index = 0;
        lfn_FilterPersonGubun(strCode);

    }else{
        radCpersonGubun.Index = 1;     // null아닌경우 인력구분 활성화하고 인력구분 ds을 해당직능에 맞게 필터하고 인력구분 사용
        radCpersonGubun.Enable = true; // 유무를 판단한다.
        lfn_FilterPersonGubun(strCode);

    }
    radCpersonGubun_OnChanged(object("radCpersonGubun"),radCpersonGubun.Value); // 인력구분에 따라 콤보의 사용 유무를 결정함
                                                                                // 
}

/*
 * 인력구분 필터링
 */
function lfn_FilterPersonGubun(sFilterGubun) {

	if(gfn_IsNull(sFilterGubun)) {   // 세부직능이 전체일 경우
        sFilterGubun = 99;
    }

	ds_oPersonGubun.UnFilter();
	var fGubun;		//필터구분(직능)
	switch(sFilterGubun)
	{
		//공공기관
		case 13 :
			fGubun = "10";   // 세부직능이 변경될때
		break;
		case 14 :
			fGubun = "10";   // 세부직능이 변경될때
		break;
		//위험물
		case 30 :
			fGubun = "30";   // 대표직능이 변경될때 
		break;  //        
		//소방기술자
		case 50 :
			fGubun = "50";   // 대표직능이 변경될때 
		break;
        default :
            fGubun = "99";  
        break;
	}

	var ls_FilterExpr = "";
    if (!gfn_IsNull(fGubun)) {
        ls_FilterExpr += "PCD=='" + trim(fGubun) + "'";
    }
    ds_oPersonGubun.Filter(ls_FilterExpr);   // ds을 해당 대표 직능에 맞게 인력구분 ds을 필터함

    if(fGubun == "99") {            // 세부직능이 전체이거나 위 조건에 만족하지 못할 경우 인력 구분을 전체로 하고 선택 못하게 비활성홤
        radCpersonGubun.Index = 0;
        radCpersonGubun.Enable = false;
        radCpersonGubun_OnChanged(object("radCpersonGubun"),radCpersonGubun.Value);
    }
}

//입력구분 전체, 선택 유무
function radCpersonGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if(strCode=="1"){
        cbxCpersonGubun1.Index=0;
        cbxCpersonGubun1.Enable=true;

	}else{
        cbxCpersonGubun1.Value = "";
        cbxCpersonGubun1.Enable=false;
	}
}



// 지부구분 변경시
function radGijun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "1") {
		cbxJibu.Enable = true;
		cbxBonbu.Enable = false;
		lbRegion.Text = "지역";
		cbxJibu_OnChanged("", cbxJibu.Value);
		radRegion_OnChanged("", radRegin.Value);
	} else if (strCode == "2") {
		cbxJibu.Enable = false;
		cbxBonbu.Enable = true;	
		lbRegion.Text = "소방서";
		cbxBonbu_OnChanged("",cbxBonbu.Value);
		radRegion_OnChanged("", radRegin.Value);
	}
}


// 소방본부 변경시
function cbxBonbu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_FilterFireStation(strCode);
	
	radRegin.Index = 1;  // 선택
	radRegion_OnChanged("", radRegin.Value);
	cbxRegion1.Index = 0;
	cbxRegion1_OnChanged("",cbxRegion1.Value);	

/*	cbxRegion2.Index = 0;
	cbxRegion3.Index = ds_oRegion.rowcount-1;*/
	
//	trace(ds_oBonbu.GetColumn(nNewIndex,"CD"));
}

/*==  소방서 조회 (소방본부 변경시)  ==*/
function lfn_FilterFireStation(sFilterGubun) {	
	ds_oFireCd.UnFilter();		
	ds_oFireCenterCd.UnFilter();
	ds_oFireCenterCd2.UnFilter();
    ds_oRegion.Clear();
    ds_oRegion.copy(ds_oFireCd);    
    
    //  필터링
	var ls_FilterExpr = "";
    if (!gfn_IsNull(sFilterGubun)) {
        ls_FilterExpr += "CTCD='" + trim(sFilterGubun) + "'";
        ds_oRegion.Filter(ls_FilterExpr);
		ds_oFireCenterCd.Filter(ls_FilterExpr);
		ds_oFireCenterCd2.Filter(ls_FilterExpr);
    }
  
}

// 소방서 변경시
function cbxRegion1_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_FilterFireCenter(strCode);
	radCenter.Index = 0;
	radCenter_OnChanged("", radCenter.Value);
	
//	trace(ds_oRegion.GetColumn(nNewIndex,"CD"));
}


// 소방서 선택시 소방서에 포함된 센터정보 필터링 
function lfn_FilterFireCenter(sFilterGubun) {	
	ds_oFireCenterCd.UnFilter();
	ds_oFireCenterCd2.UnFilter();
	
	// 필터링
	var ls_FilterExpr = "";
    if (!gfn_IsNull(sFilterGubun)) {
        ls_FilterExpr += "UPCD='" + trim(sFilterGubun) + "'";
    }    
    ds_oFireCenterCd.Filter(ls_FilterExpr);
    ds_oFireCenterCd2.Filter(ls_FilterExpr);
    
 
    // 미연계자료 항목 추가 
	if (ds_oFireCenterCd.GetColumn(ds_oFireCenterCd.lastrow ,"DATA") == "[미연계자료]") {
		ds_oFireCenterCd.AddRow(ds_oFireCenterCd.lastrow+1);
    } else {
		ds_oFireCenterCd.InsertRow(ds_oFireCenterCd.lastrow+1);
    }	
    ds_oFireCenterCd.SetColumn(ds_oFireCenterCd.lastrow,"DATA","[미연계자료]");
    ds_oFireCenterCd.SetColumn(ds_oFireCenterCd.lastrow,"CTCD",trim(sFilterGubun));
    
}


function radCenter_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode == "0") {	// 전체
        cbxMFCenter1.Enable = false;
        cbxMFCenter2.Enable = false;
        cbxMFCenter3.Enable = false;
        cbxMFCenter1.value = "";
        cbxMFCenter2.value = "";
        cbxMFCenter3.value = "";

	} else if(strCode == "1"){	//선택
		cbxMFCenter1.Enable = true;
		cbxMFCenter2.Enable = false;
		cbxMFCenter3.Enable = false;
		cbxMFCenter1.value = ds_oFireCenterCd.GetColumn(0, "CD");
		cbxMFCenter2.value = "";
		cbxMFCenter3.value = "";

	} else if(strCode == "2") {	// 범위선택
		cbxMFCenter1.Enable = false;
		cbxMFCenter2.Enable = true;
		cbxMFCenter3.Enable = true;
		cbxMFCenter1.value = "";
		cbxMFCenter2.value = ds_oFireCenterCd2.GetColumn(0, "CD");
		cbxMFCenter3.value = ds_oFireCenterCd2.GetColumn(0, "CD");
	}	
}



/*
*  강습교육 수료자(1년이내) 교육통지 제외 여부
*/
function chkTrainingExcept_OnClick(obj,strValue)
{
	if(strValue == '1') {
        CalTrainingStart.Enable = true;
        CalTrainingEnd.Enable = true;

        CalTrainingStart.Value = adddate(left(GetDate(), 8), -365);
        CalTrainingEnd.Value = left(GetDate(), 8);        
        
	} else {
	    CalTrainingStart.Enable = false;
        CalTrainingEnd.Enable = false;
        CalTrainingStart.value = "";
        CalTrainingEnd.value = "";
	}	
}


function CalTrainingStart_OnChanged(obj,strOldText,strNewText)
{
    CalTrainingEnd.Value = adddate(left(CalTrainingStart.Value, 8), 365);
}

function CalTrainingEnd_OnChanged(obj,strOldText,strNewText)
{
    CalTrainingStart.Value = adddate(left(CalTrainingEnd.Value, 8), -365);	
}
