/*
*  교육통지서 발행 이력 검색
*/
function lfn_EduNoticeHistorySearch()
{   	 	
	ds_ioHistorySearch.Clear(); 
    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust1105_1EduNoticeHistoryDaySearch", false);
	// 서버 호출 

	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioHistorySearch=ds_oEduNoticeHistoryDaySearch"	// outDs
		,"Gubun=" + quote(radGubun.Value)    // 조회구분 // args 
            + " calStartDate=" + quote(substr(calStartDate.Value,0,8))    // 작업 날짜                              
            + " calEndDate=" + quote(substr(calEndDate.Value,0,8))    // 작업 날짜            
            + " sSearchGubun=" + quote(sSearchGubun)    // 업구구분 (이력 조회, 문자발송위한 이력 조회)
            + " sJibu=" + quote(sJibu)    // 지부
		,"lfn_AftersearchCust1"
		,false
		,""
		,true
	);	
}

