function radGubunChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    ds_ioHistorySearch.Clear();

    if(radGubun.Value == 1) {
        gdCommonCode.Format = "Default";
    } else if(radGubun.Value == 0) {
        gdCommonCode.Format = "EduDate";
    }
}

/*
* 발행 이력 상세 보기
*/
function gdCommonCode_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    lfn_DetailSerch(nRow);
}

/*
* 발행 이력 상세 보기
*/
function lfn_DetailSerch(nRow)
{

    if(workGubun == "smsSend") {	
        var arrArg = Array();
            arrArg[0] = ds_ioHistorySearch.GetColumn(nRow, "EPROCDATE"); // 교육통지서 발행 날짜	 
            arrArg[1] = ds_ioHistorySearch.GetColumn(nRow, "TOTALCOUNT"); // 추출대상자수
            arrArg[2] = ds_ioHistorySearch.GetColumn(nRow, "EDUCOUNT"); // 교육대상자수 
            arrArg[3] = ds_ioHistorySearch.GetColumn(nRow, "ESCHEULE"); // 교육일시 
            arrArg[4] = ds_ioHistorySearch.GetColumn(nRow, "EGROUPID"); // 교육그룹 id 
            arrArg[5] = ds_ioHistorySearch.GetColumn(nRow, "EGET_SYSDATE"); // 발행시간
        Close(arrArg);
    } else{

        var sArg = "sEPROCDATE= " + quote(ds_ioHistorySearch.GetColumn(nRow, "EPROCDATE")); // 교육통지서 발행 날짜	
            sArg = sArg + " sEGET_SYSDATE= " + quote(ds_ioHistorySearch.GetColumn(nRow, "EGET_SYSDATE")); // 교육통지서 발행 날짜시간	
            sArg = sArg + " sTOTALCOUNT= " + quote(ds_ioHistorySearch.GetColumn(nRow, "TOTALCOUNT")); //  추출대상자수
            sArg = sArg + " sEDUCOUNT= " + quote(ds_ioHistorySearch.GetColumn(nRow, "EDUCOUNT")); //  교육대상자수 

        var arrSeatNo = Dialog("cust::frmcust1105_2MEduNotice.xml", sArg, 600, 400, "", -1, -1);
    } 
}

function lfn_DetailSerch_Ready(obj)
{
    lfn_DetailSerch(ds_ioHistorySearch.currow);
}


