function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdElecAppr.Height = nCy - gdElecAppr.Top - 4;
        gdElecAppr.Width = nCx - gdElecAppr.Left - 4;
    }
}
