/*
* 화면 로드와 동시에 교육일정 생성 자료 조회 하기
*/
function lfn_mod() {

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust1105_2EduNoticeHistoryResearchEdu", false);
	// 서버 호출 

	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioEduNoticeHistoryResearchEdu=ds_oEduNoticeHistoryResearchEdu"	// outDs
		,"EPROCDATE=" + quote(sEPROCDATE)    // 통지서 발행 일자 // args 
		+ " EGET_SYSDATE=" + quote(sEGET_SYSDATE)    // 통지서 발행 일자시간 // args 
		   //   + " rDate=" + quote(left(GetDate(), 8))    // 작업 날짜            

		,"lfn_AftersearchCust1"
		,false
		,""
		,true
	);   

}

/*
*  교육통지서 발급이력 상세조회
*/
function lfn_EduNoticeHistoryEduList(nRow)
{   	 	
	ds_ioEduNoticeHistoryDay.Clear(); 
    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust1105_2EduNoticeHistoryResearchEduList", false);
	// 서버 호출 

	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioEduNoticeHistoryResearchEduList1=ds_oEduNoticeHistoryResearchEduList"	// outDs
		,"EGROUPID=" + quote(ds_ioEduNoticeHistoryResearchEdu.GetColumn(nRow, "EGROUPID")) 
		+ " EPROCDATE=" + quote(sEPROCDATE)    // 통지서 발행 일자 
		+ " EGET_SYSDATE=" + quote(sEGET_SYSDATE)    // 통지서 발행 일자시간            // args             		                          
		,"lfn_AftersearchCust1"
		,false
		,""
		,true
	);	
}

/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AftersearchCust1(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }   
}

