/*
* 교육통지서 출력 
*/
function lfn_EduPrint(obj)
{




    ds_ioEduNoticeHistoryResearchEduList2.Clear();  // 복사할 용도
    ds_ioEduNoticeHistoryResearchEduList3.Clear();

    ds_ioEduNoticeHistoryResearchEduList2.copy(ds_ioEduNoticeHistoryResearchEduList1);  // 레포트 위한 복사
    trace("2->" + ds_ioEduNoticeHistoryResearchEduList2.count);     
    ds_ioEduNoticeHistoryResearchEduList2.Filter("EPRINTGUBUN= '1'");    // 통지서 발급 선택한것만 걸러줌
    trace("2_1->" + ds_ioEduNoticeHistoryResearchEduList2.count);     
    ds_ioEduNoticeHistoryResearchEduList3.copyf(ds_ioEduNoticeHistoryResearchEduList2);  // 통지서 발급 선택한것을 복사 담음   
    trace("3->" + ds_ioEduNoticeHistoryResearchEduList3.count);

    if(ds_ioEduNoticeHistoryResearchEduList3.count == 0){
        alert("교육통지서를 발행할 대상자를 조회 및 선택 해 주십시오.");
        return false;
    } 

    gfn_CallReportPopup("frmCust1100R01EduNotice.rex"
                ,"ds_ioEduNoticeHistoryResearchEduList3"    // ds
                ,""                  // 매개변수
                ,""                  // 수첩여부 
                ,""                  // 서브레포트 사용여부
     );      

}

function gdConnections_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	lfn_EduNoticeHistoryEduList(nRow);	
	lfn_List_count();

}

/*
* 건수 표시
*/
function lfn_List_count()
{
	var cnt = 0; // 	
	for(i = 0; i < ds_ioEduNoticeHistoryResearchEduList1.RowCount(); i++) {
        if (ds_ioEduNoticeHistoryResearchEduList1.GetColumn(i, "EPRINTGUBUN") == 1) {
            cnt += 1; // 
        }  
    }
    medSmsCnt.Value = cnt; 
 }   


