/*
*  교육통지서 발행 이력 검색
*/
function lfn_EduNoticeHistorySearch()
{   	 	
	ds_ioHistorySearch.Clear(); 
    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust1106EduNoticeHistoryDaySearch", false);
	// 서버 호출 

	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioHistorySearch=ds_oEduNoticeHistoryDaySearch"	// outDs
		,"Gubun=" + quote(radGubun.Value)    // 조회구분 // args 
		    + " GTMGNO=" + quote(cbxJibu.Value)    // 지부
            + " calStartDate=" + quote(calStartDate.Value)    // 작업 날짜                              
            + " calEndDate=" + quote(calEndDate.Value)    // 작업 날짜            
		,"lfn_AftersearchCust1"
		,false
		,""
		,true
	);	
}

