/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
    
    if (IsExistVar("sCourseCode")) {
		/*
			
		00	소방안전관리자+소방안전관리보조자
		10	소방안전관리자
		20	소방안전관리보조자
		30	위험물안전관리자
		80	위험물운송자
		40	위험물운반자
		50	소방기술자 
	
		*/
		
		var vScode = sCourseCode;
		/*
		if( sCourseCode == '10' || sCourseCode == '20'){
			vScode = "10";
		} 
		if(sCourseCode == '30' || sCourseCode == '80'){
			vScode = "30";
		}		 
		if(sCourseCode == '50'){
			vScode = "50";
		}*/
		radWorkGubun.Value = vScode;
		//trace("sCourseCode : " + sCourseCode);
		//trace("vScode : " + vScode);
		lfn_Search(vScode);		
    }
    
	if (gds_oUserInfo.GetColumn(0, "ADMINGUBUN") == "SM") {
		radWorkGubun.Enable = true;
	}
}

function Form_OnUnloadCompleted(obj) {
    Close("");
}
