/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
	var vWorkGubun = obj ;
	ds_ioSmsPattern.UnFilter();
    ds_ioSmsPattern.Filter("SPWORKGUBUN='" + vWorkGubun + "'");
    
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close("");
}
/* 공통 필수 종료 */

/*
* 종료시 전송 문자 전달
*/
function gdSmsStat_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var arrArg = Array(2);
    //arrArg[0] = ds_ioSmsPattern.GetColumn(ds_ioSmsPattern.currow, "SPPATTERN");	arrArg[0] = lfnSmsPattern(ds_ioSmsPattern.GetColumn(ds_ioSmsPattern.currow, "SPWORKGUBUN")
	                         , ds_ioSmsPattern.GetColumn(ds_ioSmsPattern.currow, "SPCODE"));
	arrArg[1] = ds_ioSmsPattern.GetColumn(ds_ioSmsPattern.currow, "SPMBCCD");                         
    Close(arrArg);
}

function radWorkGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_Search(obj);
}
