/* 사용자 이벤트 함수 */




function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdHistory.Height = nCy - gdHistory.Top - 4;
        gdHistory.Width = nCx - gdHistory.Left - 4;
    }
}
