/*
*  교육안내문 발행 이력 검색
*/
function lfn_EduDataHistorySearch()
{   	 	
	ds_ioHistorySearch.Clear(); 
    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust1108EduDataHistoryDaySearch", false);
	// 서버 호출 

	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioHistorySearch=ds_oEduDataHistoryDaySearch"	// outDs
		,"GTMGNO=" + quote(cbxJibu.Value)    // 지부
            + " calStartDate=" + quote(calStartDate.Value)    // 작업 날짜                              
            + " calEndDate=" + quote(calEndDate.Value)    // 작업 날짜 // args 
		,"lfn_AftersearchCust1"
		,false
		,""
		,true
	);	
}

////////////

/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AftersearchCust1(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }   
}

/*
*  그리드 헤더 클릭하기
*/
function lfn_SortGridAAA_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
		var lobj_Dataset = object(obj.BindDataset);
	if (nCell == 0) { // 선택
        if (obj.GetCellProp("head", nCell, "text") == 1) { // 전체가 이미 선택된 경우
            obj.SetCellProp("head", nCell, "text", 0);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "SELA", "0");
            }
        } else { // 전체가 이미 선택되지 않은 경우
            obj.SetCellProp("head", nCell, "text", 1);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "SELA", "1");
            }
        }
	} else { // 헤드 정렬
        lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex);
	}
}


/*
* 발행 이력 상세 보기
*/
function gdCommonCode_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    lfn_DetailSearch(nRow);
}

/*
* 발행 이력 상세 보기
*/
function lfn_DetailSearch(nRow)
{
	var arrArg = Array();
		arrArg[0] = ds_ioHistorySearch.GetColumn(nRow, "EHPROCDATE"); // 교육안내문 발행 날짜	 
		arrArg[1] = ds_ioHistorySearch.GetColumn(nRow, "TOTALCOUNT"); // 추출대상자수
		arrArg[2] = ds_ioHistorySearch.GetColumn(nRow, "CREATCOUNT"); // 교육안내문 발행 건수
		arrArg[3] = ds_ioHistorySearch.GetColumn(nRow, "EHSYSDATE"); // 발행시간
		arrArg[4] = ds_ioHistorySearch.GetColumn(nRow, "EHCOURSENM"); // 직능
		arrArg[5] = ds_ioHistorySearch.GetColumn(nRow, "GPNAME");	// 생성자
	Close(arrArg);

}
