/*-----------------------------------*
*     리스트 출력 버튼 선택시        *
*------------------------------------*/
function btnListPrint_OnClick(obj)
{
	var sArg  = " gb_strGubun="     + quote(Radio1.Value);//리포트선택
		sArg += " gb_strPrtGubun="  + quote(Radio2.Value);//출력선택
		sArg += " gb_strTarget="    + quote(Radio3.Value);//교육대상(실무,제연)
		sArg += " gb_JubsuStatus="  + quote(Radio4.Value);//처리일자
		sArg += " gb_strDate1="     + quote(calStartDate.Value);//처리일자(calStartDate)
		sArg += " gb_strDate2="     + quote(calEndDate.Value);//처리일자(calEndDate)
		sArg += " gb_strOrderGubun="+ quote(Radio5.Value);//정렬순서
		sArg += " gb_JibuNm="       + quote(edJibuNm1.Text);//지부명
		sArg += " gb_CourseGubun="  + quote(radCourse.Value);//직능 구분
		sArg += " gb_Course="       + quote(cbxCourse.Value);//직능
		sArg += " gb_Course1="      + quote(cbxCourse1.Value);//직능
		sArg += " gb_Course2="      + quote(cbxCourse2.Value);//직능
		sArg += " gb_Course3="      + quote(cbxCourse3.Value);//직능
		sArg += " gb_Course4="      + quote(cbxCourse4.Value);//직능
		sArg += " gb_Course5="      + quote(cbxCourse5.Value);//직능
		sArg += " gb_Course6="      + quote(cbxCourse6.Value);//직능

		//alert(sArg);
		Dialog("cust::frmcust1111PTaEduSearchList.xml", sArg, 600, 400, "", -1, -1);	
}


