/*-----------------------------------*
*   처리일자 라디오 버튼 변경시      *
*------------------------------------*/
function Radio4_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode = "Y") {
	    calStartDate.Value = substr(addDate(GetDate(), -30 ),0,8);
        calEndDate.Value   = substr(GetDate(),0,8);
	    calStartDate.Enable = true;
        calEndDate.Enable = true;        

	} else {
	    calStartDate.Value = "";
        calEndDate.Value   = "";
	    calStartDate.Enable = false;
        calEndDate.Enable = false;        

	}
}


//직능
function radCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 1) {          //선택
        cbxCourse.SetFocus();
        cbxCourse.Enable = true;
        cbxCourse1.Enable = false;
        cbxCourse2.Enable = false;
        cbxCourse3.Enable = false;
        cbxCourse4.Enable = false;
        cbxCourse5.Enable = false;
        cbxCourse6.Enable = false;        
        cbxCourse.index=0;
        cbxCourse1.index=0;
        cbxCourse2.index=0;
        cbxCourse3.index=0;
        cbxCourse4.index=0;
        cbxCourse5.index=0;
        cbxCourse6.index=0;
	} else if(strCode == 2){     //다중선택
        cbxCourse1.SetFocus();
        cbxCourse.Enable = false;
        cbxCourse1.Enable = true;
        cbxCourse2.Enable = true;
        cbxCourse3.Enable = true;
        cbxCourse4.Enable = true;
        cbxCourse5.Enable = true;
        cbxCourse6.Enable = true;        
        cbxCourse.index=0;
        cbxCourse1.index=0;
        cbxCourse2.index=0;
        cbxCourse3.index=0;
        cbxCourse4.index=0;
        cbxCourse5.index=0;
        cbxCourse6.index=0;
	} else {                    //전체
        cbxCourse.Enable = false;
        cbxCourse1.Enable = false;
        cbxCourse2.Enable = false;
        cbxCourse3.Enable = false;
        cbxCourse4.Enable = false;
        cbxCourse5.Enable = false;
        cbxCourse6.Enable = false;
        cbxCourse.index=0;
        cbxCourse1.index=0;
        cbxCourse2.index=0;
        cbxCourse3.index=0;
        cbxCourse4.index=0;
        cbxCourse5.index=0;
        cbxCourse6.index=0;
	}
} 

