function lfn_AfterSearchTrainingOrder(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    } 
}


/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj) {



    if (gb_strGubun == "1") {
		if (g_strPrtGubun == "1") {
            gfn_GridMultiSort(gdCommonCode1, 0); // 멀티 정렬을 실행한다.
		} else if (g_strPrtGubun == "2") {    
            gfn_GridMultiSort(gdCommonCode2, 0); // 멀티 정렬을 실행한다.
		}
	} else if (gb_strGubun == "2") {
		if (g_strPrtGubun == "1") {
            gfn_GridMultiSort(gdCommonCode3, 0); // 멀티 정렬을 실행한다.
		} else if (g_strPrtGubun == "2") {    
            gfn_GridMultiSort(gdCommonCode4, 0); // 멀티 정렬을 실행한다.
		}		
	} 	

}

function lfn_Excel(obj)
{
    if (gb_strGubun == "1") {
		if (g_strTarget == "0") {
            gdCommonCode1.ExportExcelEx("타지부교육입력내역 조회");
		} else if (g_strPrtGubun == "1") {    
            gdCommonCode2.ExportExcelEx("타지부교육입력내역 조회");
		}
	} else if (gb_strGubun == "2") {
		if (g_strTarget == "0") {
            gdCommonCode3.ExportExcelEx("타지부교육입력내역 조회");
		} else if (g_strPrtGubun == "1") {    
            gdCommonCode4.ExportExcelEx("타지부교육입력내역 조회");
		}		
	}
}


function btnAttend_OnClick(obj)
{
//	alert("준비중입니다.");
	var datasetlist;
    var dataGubun;  //레포트 헤더 구분
    if (gb_strGubun == "1") {
		if (g_strTarget == "0") {
			datasetlist = "ds_ioTrainingOrder1";
			dataGubun = "1";

		} else if (g_strTarget == "1") {    
			datasetlist = "ds_ioTrainingOrder2";
			dataGubun = "2";			
		}
	} else if (gb_strGubun == "2") {
		if (g_strTarget == "0") {
			datasetlist = "ds_ioTrainingOrder3";
			dataGubun = "2";			
		} else if (g_strTarget == "1") {    
			datasetlist = "ds_ioTrainingOrder4";
			dataGubun = "3";			
		}		
	} 
	gfn_CallReportPopup("frmCust1111R01EduSearchList.rex", datasetlist, "JIBU=" + quote(edJibuNm.Text) + "^GUBUN=" + gb_strGubun + "^G_TARGET=" + g_strTarget +"^G_PRTGUBUN=" + g_strPrtGubun + "^DATAGUBUN=" + dataGubun + "^TITLE=" + lbJubsu.Text) ;
}
