var g_strGubun;     //리포트선택
var g_strPrtGubun;  //출력선택
var g_strTarget;    //교육대상(실무,제연)
var g_JubsuStatus;  //처리일자
var g_strDate1;     //처리일자(calStartDate)
var g_strDate2;     //처리일자(calEndDate)
var g_strOrderGubun;//정렬순서
var g_CourseGubun;	//직능구분
var g_Course; 		//직능
var g_Course1; 		//직능
var g_Course2; 		//직능
var g_Course3; 		//직능
var g_Course4; 		//직능
var g_Course5; 		//직능
var g_Course6; 		//직능

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    //alert(ms_FormAuth);
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        //lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        //lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

	g_strGubun     = gb_strGubun  ;    //리포트선택
	g_strPrtGubun  = gb_strPrtGubun;   //출력선택
	g_strTarget    = gb_strTarget ;    //교육대상(실무,제연)
	g_JubsuStatus  = gb_JubsuStatus;   //처리일자
	g_strDate1     = gb_strDate1;      //처리일자(calStartDate)
	g_strDate2     = gb_strDate2;      //처리일자(calEndDate)
	g_strOrderGubun= gb_strOrderGubun; //정렬순서
	g_CourseGubun = gb_CourseGubun; 	//직능구분
	g_Course = gb_Course; 				//직능
	g_Course1 = gb_Course1; 			//직능
	g_Course2 = gb_Course2; 			//직능
	g_Course3 = gb_Course3; 			//직능
	g_Course4 = gb_Course4; 			//직능
	g_Course5 = gb_Course5; 			//직능
	g_Course6 = gb_Course6; 			//직능

	edJibuNm.Text  = gb_JibuNm;  //지부명
	if (g_strTarget == "0") {
	    edTarget.Text = "실무교육대상";
	} else if (g_strTarget == "1") {
	    edTarget.Text = "제연설비설치대상";
	}
	calStartDate.Value = g_strDate1;
	calEndDate.Value   = g_strDate2;


    if (gb_strGubun == "1") {
		gdCommonCode1.Visible = true;
		gdCommonCode2.Visible = false;
		lbJubsu.Text = "현지부에서 입력한 타지부 교육내역 현황통계";
		//alert("1");
		lfn_Search1();
	} else if (gb_strGubun == "2") {
		gdCommonCode1.Visible = false;
		gdCommonCode2.Visible = true;
		lbJubsu.Text = "타지부에서 입력한 교육내역 현황통계";
		//alert("2");
		lfn_Search2();
	} 	

}

function lfn_SearchParm() {
	var Parm = "";
	var Course = "";
	
	// 직능조회 	
	if(g_CourseGubun == "1") {	
		Course = g_Course;
	} else if (g_CourseGubun == "2") {
		Course = "";
		if(g_Course1 != "")
			Course += "'" + g_Course1 + "'" + ",";
		if(g_Course2 != "")
			Course += "'" + g_Course2 + "'" + ",";	
		if(g_Course3 != "")
			Course += "'" + g_Course3 + "'" + ",";	
		if(g_Course4 != "")
			Course += "'" + g_Course4 + "'" + ",";	
		if(g_Course5 != "")
			Course += "'" + g_Course5 + "'" + ",";	
		if(g_Course6 != "")
			Course += "'" + g_Course6 + "'" + ",";
			
		Course = left(Course, length(Course)-1);
	}
	
	
	Parm  = " strJibu="       + quote(gds_oUserInfo.GetColumn(0, "PDEPTCD"));   
	Parm += " strGubun="      + quote(g_strGubun); 
	Parm += " strPrtGubun="   + quote(g_strPrtGubun);   
	Parm += " strTarget="     + quote(g_strTarget);   
	Parm += " JubsuStatus="   + quote(g_JubsuStatus);   
	Parm += " strDate1="      + quote(g_strDate1);   
	Parm += " strDate2="      + quote(g_strDate2);   
	Parm += " strOrderGubun=" + quote(g_strOrderGubun);
	Parm += " courseGubun=" + quote(g_CourseGubun);
	Parm += " course=" + quote(Course);
	return Parm;
}

function lfn_Search1(obj) {

    ds_ioTrainingOrder1.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
  //tit_addCsvSearchActionInfo("cust:searchCust7040PEduSearchCount1", false);
	tit_addCsvSearchActionInfo("cust:searchCust1112PTaEduSearchStatistics1", false);

	// 파라미터
 	var sParm = lfn_SearchParm();

	//alert(sParm);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioTrainingOrder1=ds_oTrainingOrder"	// outDs
		, sParm  // args 
		,"lfn_AfterSearchTrainingOrder"
		,false
	);
}


function lfn_Search2(obj) {

    ds_ioTrainingOrder2.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
  //tit_addCsvSearchActionInfo("cust:searchCust7040PEduSearchCount2", false);
	tit_addCsvSearchActionInfo("cust:searchCust1112PTaEduSearchStatistics2", false);

	// 파라미터
 	var sParm = lfn_SearchParm();

	//alert(sParm);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioTrainingOrder2=ds_oTrainingOrder"	// outDs
		, sParm  // args 
		,"lfn_AfterSearchTrainingOrder"
		,false
	);
}



function lfn_AfterSearchTrainingOrder(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    } 
}


/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj) {



    if (gb_strGubun == "1") {
		if (g_strPrtGubun == "1") {
            gfn_GridMultiSort(gdCommonCode1, 0); // 멀티 정렬을 실행한다.
		} else if (g_strPrtGubun == "2") {    
            gfn_GridMultiSort(gdCommonCode2, 0); // 멀티 정렬을 실행한다.
		}
	} else if (gb_strGubun == "2") {
		if (g_strPrtGubun == "1") {
            gfn_GridMultiSort(gdCommonCode3, 0); // 멀티 정렬을 실행한다.
		} else if (g_strPrtGubun == "2") {    
            gfn_GridMultiSort(gdCommonCode4, 0); // 멀티 정렬을 실행한다.
		}		
	} 	

}

function lfn_Excel(obj)
{
    if (gb_strGubun == "1") {
	    gdCommonCode1.ExportExcelEx("현지부에서 입력한 타지부 교육내역 현황통계");
	} else if (gb_strGubun == "2") {
	    gdCommonCode2.ExportExcelEx("타지부에서 입력한 교육내역 현황통계");
	} 	  

}

function btnAttend_OnClick(obj)
{
	var datasetlist;

    if (gb_strGubun == "1") {

	    datasetlist = "ds_ioTrainingOrder1";
	} else if (gb_strGubun == "2") {

	    datasetlist = "ds_ioTrainingOrder2";
	} 	 
	gfn_CallReportPopup("frmCust1111R02EduSearchStatistics.rex", datasetlist, "JIBU=" + quote(edJibuNm.Text) + "^GUBUN=" + gb_strGubun + "^TITLE=" + lbJubsu.Text);

}
