function lfn_Search(obj) {
    
    ds_oEduPassList.ClearData();
	tit_ClearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust1145EduPassList", false);
       		  
	tit_callService(  
	                 ""
	               , ""  
	               , ""//inDs 
	               , "ds_oEduPassList=ds_oEduPassList" //outDs
	               , "ESMGNO=" + quote(sESMGNO)      //회차관리번호
	               , "afterSearch"  //callBack
	               , false
	               ,false
				   ,true
	               ); 
}

function afterSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);     
        return;
    }
    
	medTotalCnt.Text = ds_oEduPassList.GetCount();

    gfn_SearchMessage(gdCommonCode); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}


