 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    // btnPrint.Enable = bStatus;    

}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdEmailStat.Height = nCy - gdEmailStat.Top - 4;
        gdEmailStat.Width = nCx - gdEmailStat.Left - 4;
    }
}
