/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
    if (calSendDateStart.Value > calSendDateEnd.Value) {
        gfn_Alert("시작일자가 종료일자보다 늦습니다.");
        calSendDateStart.Value = left(GetDate(), 4)+"0101";
        calSendDateStart.SetFocus();
        return false;
    }
    if (calEduDateStart.Value > calEduDateEnd.Value) {
        gfn_Alert("시작일자가 종료일자보다 늦습니다.");
        calEduDateStart.Value = left(GetDate(), 4)+"0101";
        calEduDateStart.SetFocus();
        return false;
    }    
        ds_oEmailStat.ClearData(); // 데이타셋 초기화        
        tit_clearActionInfo();
        tit_AddSearchActionInfo("cust:searchCust2000EmailStart", false);

        tit_callService(
            ""
            ,""
            ,""	// inDs
            ,"ds_oEmailStat=ds_oEmailStat"	// outDs
            ,"GTMGNO=" + quote(cbxJibu.value)   //-- 지부
              +" CCOURSECD=" + quote(cbxCourse.value)   //-- 직능              
              +" SENDDATE1=" + quote(calSendDateStart.Value)// 발송일자1
              +" SENDDATE2=  " + quote(calSendDateEnd.Value)// 발송일자2 
              +" EDUDATE1=" + quote(calEduDateStart.Value)  // 교육일자1
              +" EDUDATE2=  " + quote(calEduDateEnd.Value)  // 교육일자2               
              +" YEAR=" + quote(edYear.Text)  //년도
            ,"lfn_AfterSearchEmailStat"
            ,false
        );
}

function lfn_AfterSearchEmailStat(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    gfn_SearchMessage(gdEmailStat);
    medEmailCnt = ds_oEmailStat.GetRowCount();	//건수
}


/*
 * 기능 : 발송 리스트 출력
 * parameter : 없음
 * return : 없음
 */
function btnPrintList_OnClick(obj)
{
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
    ds_oPrintList.ClearData(); // 데이타셋 초기화       
    tit_clearActionInfo();
    tit_addCsvSearchActionInfo("cust:searchCust2000EmailStart", false);
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,""	// inDs
        ,"ds_oPrintList=ds_oEmailStat"	// outDs
        ,"GTMGNO=" + quote(cbxJibu.value)   //-- 지부
          +" CCOURSECD=" + quote(cbxCourse.value)   //-- 직능              
          +" SENDDATE1=" + quote(calSendDateStart.Value)// 발송일자1
          +" SENDDATE2=  " + quote(calSendDateEnd.Value)// 발송일자2 
          +" EDUDATE1=" + quote(calEduDateStart.Value)  // 교육일자1
          +" EDUDATE2=  " + quote(calEduDateEnd.Value)  // 교육일자2               
          +" YEAR=" + quote(edYear.Text)  //년도
        ,"lfn_AfterPrintList"
        ,false
    );
}

/*
 * 기능 : 발송 리스트 출력 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterPrintList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    if (ds_oPrintList.GetCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }else{
        var param  = "rpStartDate="+quote(calSendDateStart.Value);
            param += "^rpEndDate="+quote(calSendDateEnd.Value);
            param += "^Jibu="+quote(cbxJibu.Text);
        gfn_CallReportPopup("frmCust2000R01EmailSendList.rex", "ds_oPrintList", param);    
    }
}
