/*
 * 기능 : 이메일 내용 상세 보기
 * parameter : 없음
 * return : 없음
 */

function lfn_ViewMailDetail(obj)
{
	var sArg = "sMailMgno=" + quote(ds_oEmailStat.GetColumn(ds_oEmailStat.currow, "ESHMGNO"));  //이메일관리번호
	Dialog("cust::frmcust1201PMailDetailViewerPopup.xml", sArg, 1000, 700, "", -1, -1);	//이메일 상세보기 팝업을 호출한다.
}






