/*==  초기화 ==*/
function lfn_Cancel(obj)
{
	radGijun.Index = 0;
	cbxJibu.Index = 0;
	cbxBonbu.Index = 0;
	radMemberGubun.Index = 0;
	radGijun_OnChanged("","1");
	radRegion_OnChanged();
	radCourse_OnChanged();
	radEduYmdGubun_OnChanged();
	radMemberYmdGubun_OnChanged();
	
}



function lfn_Print(obj)
{
	lfn_Search();
	
	if(ds_EduList.rowcount == 0){
			alert("데이터가 없습니다.");
			return;
		}
	
	var info = "";
	if (radGijun.Value == "1") {
		info = cbxJibu.Text + "지부";
	} else {
		info = cbxBonbu.Text + "소방본부";
	}
	gfn_CallReportPopup("frmCust1210RegionEduReport.rex"
					  , "ds_EduList"
					  , "INFO=" + info + "^TITLE=지역별 교육 이수/미이수 현황"
					  , true);
	                  
}


// 조회시
function lfn_Search(obj) {
	
	tit_clearActionInfo(this);
	tit_addCsvSearchActionInfo("cust:searchCust1210MRegionEduReport", false);
	
	var COURSEN = "";
	
	if(cbxCourse1.Value != "")
		COURSEN += "'" + cbxCourse1.Value + "'" + ",";
	if(cbxCourse2.Value != "")
		COURSEN += "'" + cbxCourse2.Value + "'" + ",";	
    if(cbxCourse3.Value != "")
		COURSEN += "'" + cbxCourse3.Value + "'" + ",";	
	if(cbxCourse4.Value != "")
		COURSEN += "'" + cbxCourse4.Value + "'" + ",";	
	if(cbxCourse5.Value != "")
		COURSEN += "'" + cbxCourse5.Value + "'" + ",";	
	if(cbxCourse6.Value != "")
		COURSEN += "'" + cbxCourse6.Value + "'" + ",";			
		
	COURSEN = left(COURSEN, length(COURSEN)-1);
	
	var parValue = "GUBUN=" + quote(radGijun.Value);    // 지부 (1), 소방본부(2) 구분
	parValue += " JIBU=" + quote(cbxJibu.Value);      // 지부 
	parValue += " BONBU=" + quote(substr(cbxBonbu.Value,0,2));    // 소방본부
	parValue += " REGIONGUBUN=" + quote(radRegion.Value);  // 소방서,지역 전체 , 선택 구분
	parValue += " REGION1=" + quote(cbxRegion1.Value);     // 지역 소방서 1
	parValue += " REGION2=" + quote(cbxRegion2.Value);     // 지역 소방서 2
	parValue += " MEMBERGUBUN=" + quote(radMemberGubun.Value);    // 고객구분
	parValue += " COURSEGUBUN=" + quote(radCourse.Value);     // 직능 구분
	parValue += " COURSE=" + quote(cbxCourse.Value);    // 단일 직능
	parValue += " COURSEN=" + quote(COURSEN);  // 다중직능
	parValue += " EDUGUBUN=" + quote(radEduYmdGubun.Value);    // 교육일자 구분
	parValue += " EDUDATE1=" + quote(calEduYmdFr.Value);    // 교육일자 검색 시작일
	parValue += " EDUDATE2=" + quote(calEduYmdTo.Value);    // 교육일자 검색 종료일
	parValue += " FMGUBUN=" + quote(radMemberYmdGubun.Value);  // 선임일자 구분
	parValue += " FMDATE1=" + quote(calMemberYmdFr.Value);   // 선임일자 검색 시작일
	parValue += " FMDATE2=" + quote(calMemberYmdTo.Value);  // 선임일자 검색 종료일 
	parValue += " EXCEPTGUBUN=" + quote(radExceptGubun.Value);  // 관리제외대상 구분 
	parValue += " SUCCESSIONGUBUN=" + quote(radSuccessionGubun.Value);  // 교육이관여부 
	
	tit_callService(  
	                 ""
	               , ""   
	               , //inDs 
	               , "ds_EduList=ds_oEduList" //outDs
	               , parValue
	               , "afterSearch"  //callBack
	               , false
	               ,false
				   ,true
	               ); 	
}

// 조회 후 callback function afterSearch(errCode, errMsg){

	if(errCode != 0 ){
		alert("오류가 발생했습니다.");
	}
	else{
		
	}}

