#include "LIB::cust.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    gfn_SearchJibu(cbxJibu, "Y", "선택"); // 지부 코드 조회


	medProcDate.Value = left(addDate(left(getDate(), 6) + "01", -1), 6); //left(getDate(), 6);
    cbxJibu.SetFocus();

}

/**
 * 폼 사이즈
 */
function Form_OnSize(obj,nCx,nCy,nState) {
}

/**
 * 버튼 실행 권한
 */
function lfn_SetFormAuth(bStatus) {
}


/////////////////////////////////////////////////////////////////////////////////
// 기본 버튼
/////////////////////////////////////////////////////////////////////////////////

function lfn_Save(obj) {

    if (gfn_IsNull(cbxJibu.Value)) {
        gfn_Alert("지부를 선택해 주십시오.");
        return;
    }

/*	if (toNumber(left(getDate(), 6)) - toNumber(medProcDate.Value) != 1) {
        gfn_Alert("전월자료만 재생성 가능합니다.");
        return;	
	}
*/    
    if (!gfn_Confirm(left(medProcDate.Value, 4)+"년 "+
					right(medProcDate.Value, 2)+"월 업무통계[실무교육현황]을 재생성 하시겠습니까?")) {
        return;
    }


    var sProcYear = toNumber(left(medProcDate.Value, 4));
    var sProcMonth = toNumber(right(medProcDate.Value, 2));

    sProcMonth++;
    if (sProcMonth > 12) {
        sProcMonth = 1;
        sProcYear++;
    }

    var sPorcDate = addDate(sProcYear + lpad(sProcMonth, "0", 2) + "01", -1);

    ds_iReprodection.ClearData();
    ds_iReprodection.InsertRow(0);
    ds_iReprodection.SetColumn(0, "STARTDATE", left(medProcDate.Value, 4) + "0101");
    ds_iReprodection.SetColumn(0, "PROCDATE", sPorcDate);
    ds_iReprodection.SetColumn(0, "GTMGNO", cbxJibu.Value);

    tit_clearActionInfo();
    tit_addSingleActionInfo("cust:searchCust1220EduMonthReprodection", "", 0, "");
    tit_callService(
        ""
        ,""
        ,"ds_iReprodection=ds_iReprodection" // inDs
        ,""	// outDs
        ,""	// args 
        ,"lfn_AfterSave"
        ,false
        ,false
        ,true
    );

}


/////////////////////////////////////////////////////////////////////////////////
// 이벤트
/////////////////////////////////////////////////////////////////////////////////

function medProcDate_OnCharChanged(obj,strPreText,strPostText) {
	if (right(medProcDate.Value, 2) < "01") {
        medProcDate.Value = left(medProcDate.Value, 4) + "01";
	} else if (right(medProcDate.Value, 2) > "12") {
        medProcDate.Value = left(medProcDate.Value, 4) + "12";
	}
}


/////////////////////////////////////////////////////////////////////////////////
// 공통
/////////////////////////////////////////////////////////////////////////////////

/**
 * 저장 후 처리
 */
function lfn_AfterSave(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        gfn_Alert("오류가 발생되었습니다. 정보지원과에 문의하시기 바랍니다.");
        return "Transaction Error!";
    }

    gfn_Alert("업무통계[실무교육현황] 재생성이 완료되었습니다.");
}


// 마감
function btnFinishProc_OnClick(obj)
{
/*	if (toNumber(left(getDate(), 6)) - toNumber(medProcDate.Value) != 1) {
        gfn_Alert("전월자료만 마감처리 가능합니다.");
        return;	
	}
*/	
    if (!gfn_Confirm(left(medProcDate.Value, 4)+"년 "+
					right(medProcDate.Value, 2)+"월 업무통계[실무교육현황]을 마감처리 하시겠습니까?")) {
        return;
    }

    ds_iCusEduChk.ClearData();
    ds_iCusEduChk.InsertRow(0);
    ds_iCusEduChk.SetColumn(0, "CECCHKGUBUN", "0");

    var sRtn = lfn_FinishProc();
    if (sRtn) {
        gfn_Alert("마감처리 되었습니다.");
    }
}

/**
 * 마감취소
 */
function btnFinishCancel_OnClick(obj)
{
/*	if (toNumber(left(getDate(), 6)) - toNumber(medProcDate.Value) != 1) {
        gfn_Alert("전월자료만 마감취소 가능합니다.");
        return;	
	}
*/	
    if (!gfn_Confirm(left(medProcDate.Value, 4)+"년 "+
					right(medProcDate.Value, 2)+"월 업무통계[실무교육현황]을 마감취소 하시겠습니까?")) {
        return;
    }

    ds_iCusEduChk.ClearData();
    ds_iCusEduChk.InsertRow(0);
    ds_iCusEduChk.SetColumn(0, "CECCHKGUBUN", "1");

    var sRtn = lfn_FinishProc();
    if (sRtn) {
        gfn_Alert("마감취소되었습니다.");
    }

}


/**
 * 처리
 */
function lfn_FinishProc() {

    if (gfn_IsNull(cbxJibu.Value)) {
        gfn_Alert("지부를 선택해 주십시오.");
        return false;
    }

    var sProcYear = left(medProcDate.Value, 4);
    var sProcMonth = right(medProcDate.Value, 2);

    ds_oCusEduChk.ClearData();
    tit_clearActionInfo();
    tit_addSearchActionInfo("cust:searchCust1220CusEduChk", false);
    tit_callService(
        ""
        ,""
        ,""	// inDs
        ,"ds_oCusEduChk=ds_oCusEduChk"	// outDs
        ,"CECGTMGNO=" + quote(cbxJibu.Value)
            + " CECYEAR=" + quote(sProcYear)
            + " CECMONTH=" + quote(sProcMonth)	// args
        ,""
        ,false
        ,false
        ,true
    );

    if (ds_oCusEduChk.GetRowCount() > 0 && ds_oCusEduChk.GetColumn(0, "CECCLOSEGUBUN") == "0") { //마감일 경우
        gfn_Alert(sProcYear + "년 " + sProcMonth + "월 " + "현황은 이미 마감되었습니다.");
        return false;
    }

    ds_iCusEduChk.SetColumn(0, "CECCLOSEGUBUN", "1");
    ds_iCusEduChk.SetColumn(0, "CECGTMGNO", cbxJibu.Value);
    ds_iCusEduChk.SetColumn(0, "CECYEAR", sProcYear);
    ds_iCusEduChk.SetColumn(0, "CECMONTH", sProcMonth);

    tit_clearActionInfo();
    tit_addSingleActionInfo("cust:insertCust1220CusEduChk", "", 0, "");
    tit_callService(
        ""
        ,""
        ,"ds_iCusEduChk=ds_iCusEduChk"	// inDs
        ,""	// outDs
        ,""	// args 
        ,""
        ,false
        ,false
        ,true
    );

    return true;
}
