/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    // 공통 코드 조회

    gfn_SearchAllJibu(cbxJibu, "Y", "선택"); // 지부 코드 조회
        
    gfn_SearchCustomerCourseFullName(ds_oCourse, "0");				// 대표고객직능 조회
    
    //gfn_SearchCustomerDetailCourse("",ds_oDetailCourse, "0", "전체");
    gfn_SearchCustomerDetailCourseFullName("", ds_oDetailCourse, "0", "전체");			// 세부고객직능 조회

    // 교육접수가능 직능만 남김
    for (var i = 0; i <= ds_oCourse.GetRowCount(); i++) {
    
		if ((ds_oCourse.GetColumn(i, "CD") == "60") 
		
		|| (ds_oCourse.GetColumn(i, "CD") == "70")
		
		|| (ds_oCourse.GetColumn(i, "CD") == "90")
		
		) {
		
			ds_oCourse.DeleteRow(i);
			
			i = i - 1;
		}
    }
    
    cbxCourse_OnChanged();
    
    lfn_FilterDetailCourse(cbxCourse.Value);	// 세부직능필터

	radSearchGubun.Value = "0";
	
	edEduOrderStart.Enable = true;
	
	edEduOrderEnd.Enable = true;
	
	calEduStart.Enable = false;
	
	calEduEnd.Enable = false;

	// 실무교육일정관리에서 더블클릭하여 조회된 자료이면
	// if (!gfn_isNull(sJibu)) {

		// cbxJibu.Value = sJibu;
		
		// edEduYear.Text = sYear;
		
		// cbxCourse.Value = sCourse;
		
		// edEduOrderStart.Text = sOrder;
		
		// edEduOrderEnd.Text = sOrder;
		
		// radSunapYn.Value = sSunapGubun;
		
		// lfn_Search(obj);
	
	// } else {
	
		cbxCourse.Value = "10";	
		edEduYear.Text = left(GetDate(), 4);	
		edEduOrderStart.Text = "001";	
		edEduOrderEnd.Text = "001";	
		edEduOrderStart.SetFocus();

	// }
	
	
}

function Form_OnSize(obj,nCx,nCy,nState) {
    if (gs_IsResizingForm == "Y") {
        gdEduJubsu.Height = nCy - gdEduJubsu.Top - 4;
        gdEduJubsu.Width = nCx - gdEduJubsu.Left - 4;
    }
}

/*
 * 기능 : 조회
 */
var course = "";
function lfn_Search(obj) {
    if (!gfn_FormValidate(this)) { // 조회 Validation Check
        return false;
    }
    ds_oEduJubsu.ClearData(); // 데이타셋 초기화
    
    // 조회이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                
                + ",세부직능=" + quote(cbxDetailCourse.Text)
                + ",년도=" + quote(edEduYear.Text)
                + ",회차시작=" + quote(edEduOrderStart.Text)
                + ",회차종료=" + quote(edEduOrderEnd.Text)
                + ",수납구분=" + quote(radSunapYn.Text)
                        
                + ",조회구분=" + quote(radSearchGubun.Text)
                
                + ",이수일자시작=" + quote(left(calEduStart.Value,8))
                + ",이수일자종료=" + quote(left(calEduEnd.Value,8));
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );
    course = cbxCourse.Text;  // 레포트에 직능이름 보내는 파라메터값
    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust1300EduJubsuStat", false);
	// 서버 호출 
	
	// ESCERTIYN 추가(20221018): 인증합격여부 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oEduJubsu=ds_oEduJubsu"	// outDs
		,"ESGTMGNO=" + quote(cbxJibu.Value)
            + " ESCOUSECD=" + quote(cbxCourse.Value)
            
            + " ESDETAILCOUSECD=" + quote(cbxDetailCourse.Value)
            + " ESYEAR=" + quote(edEduYear.Text)
            + " ESEDUORDERSTART=" + quote(edEduOrderStart.Text)
            + " ESEDUORDEREND=" + quote(edEduOrderEnd.Text)
            + " SUNAPGUBUN=" + quote(radSunapYn.Value)            
            + " SEARCHGUBUN=" + quote(radSearchGubun.Value)
             
            + " ESEDUDATESTART=" + quote(left(calEduStart.Value,8))
            + " ESEDUDATEEND=" + quote(left(calEduEnd.Value,8))
            
            + " ESCERTIYN=" + quote(cbxCertiYN.Value)
             
             // args
			,"lfn_AfterSearchEduJubsu"
			,false
	);
}

/*
 * 기능 : 접수자목록출력
 */
function lfn_Print(obj) {
    if (ds_oEduJubsu.RowCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }
   
    if(!gfn_CheckChangedGrid(this))  //선택한것이 있는지 체크 메세지 보임
    { alert("출력할 인원을 선택해 주십시오.");      
        return false;
    }
        ds_ioReport.copy(ds_oEduJubsu);  // 1) 원본 복사           
    ds_ioReport.Filter("SEL = '1'"); // 2) 복사 후 필터함 
    
    ds_ioReportFilter.copyf(ds_ioReport);  // 3) 필터한 값을 실제 사용 위해 다른 곳에 복사     
    ds_ioReport.ClearData(); // 4) 데이타셋 초기화  레포트 출력위해 필터된 2)번의 데이터셋을 초기화함    
       
    // 출력이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edEduYear.Text)
                + ",회차시작=" + quote(edEduOrderStart.Text)
                + ",회차종료=" + quote(edEduOrderEnd.Text)
                + ",납부구분=" + quote(radSunapYn.Text)

                
                + ",조회구분=" + quote(radSearchGubun.Text)
                
                + ",이수일자시작=" + quote(left(calEduStart.Value,8))
                + ",이수일자종료=" + quote(left(calEduEnd.Value,8));
    gfn_InsertSearchPrintHistory(
        "P"
        , this.ID
        , "접수자목록출력"
        , ls_HistoryArg
    );
    
    //ds_ioReportFilter.Sort("EJHJUBSUNO:A");
    // to do Validation Check
    gfn_CallReportPopup("frmCust1300R01EduJubsuList.rex"
                      , "ds_ioReportFilter"
                      , "COURSENM="+course);
}

/*
 * 기능 : 참가원일괄출력
 */
function lfn_PrintEdu(obj) {
var ls_RexFile = "";

    if (ds_oEduJubsu.RowCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }
    
    if(!gfn_CheckChangedGrid(this))  //선택한것이 있는지 체크 메세지 보임
    { alert("출력할 인원을 선택해 주십시오.");      
        return false;
    }
    
    if (!gfn_Confirm("참가원을 일괄출력하시겠습니까?")) {
        return false;
	}
    ds_ioReport.copy(ds_oEduJubsu);  // 1) 원본 복사           
    ds_ioReport.Filter("SEL = '1'"); // 2) 복사 후 필터함 
    
    ds_ioReportFilter.copyf(ds_ioReport);  // 3) 필터한 값을 실제 사용 위해 다른 곳에 복사     
    ds_ioReport.ClearData(); // 4) 데이타셋 초기화  레포트 출력위해 필터된 2)번의 데이터셋을 초기화함    
    	

    // 출력이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edEduYear.Text)
                + ",회차시작=" + quote(edEduOrderStart.Text)
                + ",회차종료=" + quote(edEduOrderEnd.Text)
                + ",납부구분=" + quote(radSunapYn.Text)

                
                + ",조회구분=" + quote(radSearchGubun.Text)
                
                + ",이수일자시작=" + quote(left(calEduStart.Value,8))
                + ",이수일자종료=" + quote(left(calEduEnd.Value,8));
    gfn_InsertSearchPrintHistory(
        "P"
        , this.ID
        , "참가원일괄출력"
        , ls_HistoryArg
    );
    
    //ds_ioReportFilter.Sort("EJHJUBSUNO:A");

    if (cbxCourse.Value == "80") { //운송자
        ls_RexFile = "frmCust1031R04PInetEduStatistics.rex";
    } else {
        ls_RexFile = "frmCust1031R03PInetEduStatistics.rex";
    }

    // to do Validation Check
    gfn_CallReportPopup(ls_RexFile
    
						, "ds_ioReportFilter"	
						, "참가원");
}
